'use strict';

angular
    .module('airfi.components.crew')
    .controller('CrewCatalogController', function crewCatalogController($scope, $timeout, $location, Shop, $rootScope,
        AirfilyticsService, FlightService, StockService, $routeParams, CrewCatalogService, $filter, $log) {
        const galleyUrl = '/crew/galley';
        $scope.categories = [];
        $scope.moreCategories = [];
        $scope.showCategories = [];
        $scope.expandCategories = false;
        $scope.showCategoriesText = 'MORE';
        $scope.products = [];
        $scope.product = null;
        $scope.productGroups = {};
        $scope.lineItems = {};
        $scope.quantity = 1;
        $scope.maxItemCount = 5;
        $scope.validQuantity = true;
        $scope.filter = {};
        $scope.headerHeight = null;
        $scope.catalogCategoryHash = {};
        $scope.imageUrl = '';
        $scope.initing = false;
        $scope.catalogs = [];
        $scope.translations = {};
        $scope.expectations = {};
        $scope.currentCategory = null;
        $scope.selectedOrderId = null;
        $scope.groupBy = null;
        $scope.module = 'shop';
        $scope.currentOrder = null;
        $scope.currentCartValue = 0;

        //Controller Methods
        $scope.activate = activate;
        $scope.getOrderedQuantity = getOrderedQuantity;
        $scope.addToCart = addToCart;
        $scope.getPaymentMode = getPaymentMode;
        $scope.checkout = checkout;

        //Controller Methods
        $scope.getProductImageUrl = getProductImageUrl;

        $scope.getHeaderHeight = function () {
            return $scope.headerHeight;
        };

        $scope.loadCategories = function (tab) {
            if ($scope.catalogCategoryHash[tab]) {
                $scope.categories = JSON.parse(JSON.stringify($scope.catalogCategoryHash[tab]));
                $scope.sendCatalogSelectedEvent(tab);
            } else {
                for (var catalog in $scope.catalogCategoryHash) {
                    $scope.categories = _.union($scope.categories, $scope.catalogCategoryHash[catalog]);
                }
            }
            $scope.categories.sort();
            $scope.currentCategory = $scope.categories[0];
        };

        $scope.loadTabContent = function (tab) {
            $scope.groupBy = null;
            $scope.filter = {};
            $scope.categories = [];
            $scope.currentTab = tab;
            $scope.expandCategories = false; // To be removed when we remove dom manipulation from the shop.

            switch (tab) {
            case 'all':
                $scope.groupBy = 'category';
                $scope.loadCategories(tab);
                break;

            case 'favorites':
                $scope.filter = {
                    favorite: true
                };
                break;

            default:
                if ($scope.catalogCategoryHash[tab]) {
                    $scope.filter = {
                        catalog: tab
                    };
                    $scope.groupBy = 'category';
                    $scope.loadCategories(tab);
                }
                break;
            }

            $scope.showAllCategories();
        };

        //Returns true if the product is disabled else returns false
        $scope.isDisabled = function (product) {
            return !($scope.catalogCategoryHash[product.catalog] && _.indexOf($scope.catalogCategoryHash[product.catalog],
                product.category) > -1);
        };

        $scope.showAllCategories = function () {
            $timeout(function () {
                $scope.moreCategories = [];
                $scope.showCategories = JSON.parse(JSON.stringify($scope.categories));
                $scope.showCategories.sort();
            });
        };

        $scope.hideAllCategories = function () {
            $scope.moreCategories = [];
            $scope.showCategories = [];
            $scope.moreCategories = JSON.parse(JSON.stringify($scope.categories));
            $scope.$apply();
        };


        function getPaymentMode() {
            $('#paymentPrompt')
                .foundation('reveal', 'open');
        }


        //Appends the cart created by the crew to the exsisting order.
        //Update's product quantities if they are already present.
        //If its a new product will merge it to the current order.
        function checkout(method) {
            var items = [];
            var totalItems = 0;
            var totalFormatted = 0;
            //check if the items are already in the order; if thats the case just update the quantity
            //and pull out the item from the cart.
            for (var cartLineItem in $scope.lineItems) {
                var product = Shop.getProduct(cartLineItem);
                _.forEach($scope.currentOrder.line_items, function (orderLineItem, index) {
                    if (orderLineItem.product._id === product._id) {
                        orderLineItem.quantity = $scope.lineItems[cartLineItem];
                        delete $scope.lineItems[cartLineItem]
                    }
                })
            }
            //for the rest of the item add them to the order
            for (var item in $scope.lineItems) {
                items.push({
                    'product': Shop.getProduct(item),
                    'quantity': $scope.lineItems[item],
                    'number_of_complementary_products': 0,
                    'delivery_status': 'ordered'
                });
                totalItems += $scope.lineItems[item];
            }
            //update order level data
            $scope.currentOrder.line_items = $scope.currentOrder.line_items.concat(items);
            $scope.currentOrder.total_items = parseInt($scope.currentOrder.total_items) + parseInt(totalItems);
            totalFormatted = $filter('number')($scope.currentCartValue, 2);
            $scope.currentOrder.total_purchase_value = parseFloat($scope.currentOrder.total_purchase_value) + parseFloat(
                totalFormatted);
            $scope.currentOrder.total_purchase_value_as_string = 'â‚¬' + $scope.currentOrder.total_purchase_value;

            //Persist order
            Shop.updateOrder($scope.currentOrder)
                .then(function (data) {

                    _.defer(function () {
                        $scope.currentCartValue = 0;
                        $scope.lineItems = {};
                        $scope.total = 0;
                        $scope.currentOrder = null;
                        $location.path(galleyUrl);
                        $scope.$apply();
                    });
                });
            $location.path(galleyUrl);



        }

        //Update the cart with the items that are purchased by the user.
        //This is later merged with the current order to ammend the order
        function addToCart(product) {
            var isQuantityValid = validateQuantity(product);
            if (isQuantityValid) {
                $scope.processing = true;
                $scope.lineItems[product._id] = ($scope.lineItems[product._id] || 0) + $scope.quantity;
                Shop.updateCart($scope.lineItems)
                    .then(function () {
                        $scope.lineItems = Shop.getCart()
                            .lineItems;
                        $scope.addedToCartEvent(product);
                        calculateCartValue();
                    }, function () {
                        if ($scope.lineItems[product._id] === $scope.quantity) {
                            delete $scope.lineItems[product._id];
                        } else {
                            $scope.lineItems[product._id] = $scope.lineItems[product._id] - $scope.quantity;
                        }
                    });
            } else {
                $log.error('Cannot add item, as max number of items are already in Cart');
            }
        }



        function getOrderedQuantity(productId) {
            return $scope.lineItems[productId] || 0;
        }

        $scope.resetQuantity = function () {
            $scope.processing = false;
            $scope.quantity = 1;
        };


        function validateQuantity(product) {
            var validQuantity = false;
            var productQuantity = ($scope.lineItems[product._id] || 0) + $scope.quantity;
            if (productQuantity <= $scope.maxItemCount && $scope.quantity > 0) {
                validQuantity = true;
            }
            return validQuantity;
        }


        $scope.setCurrentCategory = function (category) {
            $scope.currentCategory = category;
        };

        /*-------------------------------------AirFiLytics Related Code-----------------------------------------------*/

        $scope.sendShopEnteredEvent = function () {
            AirfilyticsService.send('Entered Shop', {
                'properties': {}
            });
        };

        function updateShopStatus() {
            if (FlightService.flightConfig) { // Crew may not have set the flight config in admin area when this code runs
                $scope.shopEnabled = FlightService.flightConfig.shopEnabled;
                $scope.shopDisabledReason = FlightService.flightConfig.shopDisabledReason;
            }
        }

        $scope.sendCatalogSelectedEvent = function (catalog) {
            AirfilyticsService.send('Selected Shop Catalogue', {
                'properties': {
                    'catalog': catalog
                }
            });
        };

        $scope.sendProductViewedEvent = function () {
            AirfilyticsService.send('Viewed Product', {
                'properties': {
                    'productId': $scope.product._id,
                    'product': $scope.product.short_description,
                    'sku': $scope.product.product_id,
                    'category': $scope.product.category,
                    'catalog': $scope.product.catalog,
                    'unitPrice': $scope.product.price
                }
            });
        };

        $scope.sendProductLikedEvent = function () {
            AirfilyticsService.send('Favorited Product', {
                'properties': {
                    'productId': $scope.product._id,
                    'product': $scope.product.short_description,
                    'sku': $scope.product.product_id,
                    'category': $scope.product.category,
                    'catalog': $scope.product.catalog,
                    'unitPrice': $scope.product.price
                }
            });
        };

        $scope.addedToCartEvent = function (product) {
            AirfilyticsService.send('Added Product', {
                'properties': {
                    'productId': product._id,
                    'product': product.short_description,
                    'sku': product.product_id,
                    'category': product.category,
                    'catalog': product.catalog,
                    'unitPrice': product.price,
                    'quantity': $scope.quantity,
                    'totalAmount': product.price * $scope.quantity
                }
            });
        };

        /*------------------------------------------------------------------------------------------------------------*/
        function getStockLevels() {
            StockService.getOutOfStockProducts()
                .then(function (outOfStockProducts) {
                    $scope.outOfStockProducts = outOfStockProducts;
                });
        }

        //fetches the enabled catalogs from the catalogCategoryHash
        function getCatalogs() {
            $scope.catalogs = [];
            for (var catalog in $scope.catalogCategoryHash) {
                if ($scope.catalogCategoryHash[catalog].length > 0) {
                    $scope.catalogs.push(catalog);
                }
            }
        }

        function processRouteParams() {
            var nextPath = null;

            //check whether the catalog is present in the URL
            if ($routeParams.catalog) {
                //check whether the catalog in URL is a valid one
                if (
                    $scope.catalogCategoryHash[$routeParams.catalog]
                    && $scope.catalogCategoryHash[$routeParams.catalog].length > 0
                ) {
                    // check if category is present in URL and if present then
                    // check that the category exist in the specified catalog
                    if (
                        $routeParams.category
                        && $scope.catalogCategoryHash[$routeParams.catalog].indexOf($routeParams.category) >=0
                    ) {
                        $scope.customFilter = {
                            category: $routeParams.category,
                            product_id: $routeParams.product
                        };
                    } else {
                        // redirect to catalog page if category is not present in the URL
                        // or the present category is not a valid one.
                        nextPath = '/' + $scope.module + '/' + $routeParams.catalog;
                        /*
                            we will not be stuck in an eternal loop since
                            $location.path will not load is the URL is the same ++.

                        */
                        $location.path(nextPath);
                        $location.replace();
                    }
                } else {
                    // as $scope.module is pointing to shop so this redirects to shop page
                    nextPath = '/' + $scope.module;
                    $location.path(nextPath);
                    $location.replace();
                }
                $scope.loadTabContent($routeParams.catalog);

            } else {
                /*
                    This means the $location.path() is #/shop. Or, we are on shop page:
                    Therefore,
                        if only one catalog is present then show it
                        else, show all catalogs
                */

                if ($scope.catalogs.length === 1) {
                    nextPath = '/' + $scope.module + '/' + $scope.catalogs[0];
                    $location.path(nextPath);
                    $location.replace();
                } else {
                    $scope.loadTabContent('all');
                }
            }
        }


        //Calculates the totalAmount for the items in the cart
        //Called everytime the item is added to the cart; or on init if the cart is prepopulated.
        function calculateCartValue() {
            $scope.currentCartValue = 0;
            _.forEach($scope.products, function (product) {
                if ($scope.lineItems[product._id]) {
                    var quantity = $scope.lineItems[product._id];
                    var price = product.raw_price;
                    $scope.currentCartValue += (quantity * price);
                }
            })
        }

        function getProductImageUrl(product) {
            return Shop.getProductImageUrl(product, $scope.defaultProductImage);
        }

        function loadShop() {
            $scope.products = Shop.getProducts();
            $scope.catalogCategoryHash = Shop.getShopSettings();
            getCatalogs();
            // $scope.lineItems = Shop.getCart().lineItems || {};
            // $scope.getLikes();
            $scope.translations = Shop.getTranslations();
            $scope.expectations = Shop.getDeliveryExpectations();
            $scope.initing = false;


            if (!!$scope.lineItems && !!$scope.products) {
                calculateCartValue();
            }

            processRouteParams();

            const onFavIconSelected = $rootScope.$on('favoriteIconSelected', function () {
                $scope.loadTabContent('favorites');
            });
            $scope.sendShopEnteredEvent();
            $scope.imageUrl = '';

            getStockLevels();

            StockService.onStockUpdate(function (productId, isOutOfStock) {
                if (isOutOfStock) {
                    $scope.outOfStockProducts[productId] = true;
                } else {
                    delete $scope.outOfStockProducts[productId];
                }
            });

            StockService.onStockReset(function () {
                $scope.outOfStockProducts = {};
            });

            const onConnEstablished = $rootScope.$on('connectionEstablished', function () {
                getStockLevels();
            });

            const onShopSettingsChanged = $rootScope.$on('shopSettingsChanged', function () {
                $scope.catalogCategoryHash = Shop.getShopSettings();
                //update the catalog array to have only active catalogs
                getCatalogs();
                processRouteParams();
            });

            $rootScope.$on('$destroy', () => {
                onFavIconSelected();
                onConnEstablished();
                onShopSettingsChanged();
            })
        }

        function activate() {
            $scope.currentOrder = CrewCatalogService.getOrderForModification();
            if (!!!$scope.currentOrder) {
                $location.path(galleyUrl);
            }
            if (!!$scope.currentOrder) {
                _.forEach($scope.currentOrder.line_items, function (lineItem) {
                    $scope.lineItems[lineItem.product._id] = lineItem.quantity
                });

                $scope.selectedOrderId = $scope.currentOrder._id;
                _.defer(function () {
                    $scope.$apply(function () {
                        $scope.currentCartValue = 0;

                    })
                })
                // var context = this;
                if (Shop.initialised) {
                    loadShop();
                } else {
                    $scope.initing = true;
                    Shop.init()
                        .then(function () {
                            loadShop();
                        }, function (err) {});
                }
                FlightService.init().then(updateShopStatus);
                const onUpdateFlightConfig = $rootScope.$on('UPDATED_FLIGHT_CONFIG', updateShopStatus);
                $rootScope.$on('$destroy', () => {
                    onUpdateFlightConfig();
                })
            }
        }



        // activate();
        // this.init();
    });

'use strict';
angular
    .module('airfiApp')
    .service('CrewCatalogService', function crewCatalogService() {
        var orderForModification = null;
        return {
            saveOrderForModification: saveOrderForModification,
            getOrderForModification: getOrderForModification,
            clearOrderForModification: clearOrderForModification
        }

        function saveOrderForModification(order) {
            orderForModification = order;
        }

        function getOrderForModification() {
            return orderForModification;
        }

        function clearOrderForModification() {
            orderForModification = null;
        }
    });

((angular) => {
  angular
    .module('airfi.components.crew') 
    .config(crewStates)
    .run(stateChangeStart)


  function stateChangeStart($rootScope, $injector, CrewServiceV2, $log) {

    const stateChangeListenter = $rootScope.$on('$stateChangeStart', function (event, toState, toParams) {
      let currentState = toState;
      let currentComponent = toState.component;
      let showFlightInfoEmptyModalArrayComponents = CrewServiceV2.showFlightInformationEmptyModal();
      if (showFlightInfoEmptyModalArrayComponents.includes(currentComponent)) {

        callMiddlewares(event, currentState);

        function callMiddlewares(event, state) {
          if (state && state.hasOwnProperty('middleware')) {
            if (typeof currentState.middleware === 'object') {
              angular.forEach(state.middleware, function (middleWare) {
                callMiddleware(middleWare, event);
              });
              return;
            }
          }
        }

        function callMiddleware(middleWare, event) { 
          try {
            $injector.get(middleWare).run(event);  
          } catch (e) {
            $log.log('the factory : ' + middleWare + ' does not exist');
          }
        };

      }
    });

  }




