angular
    .module('airfi.components.crew')
    .factory('flightInfoMiddleware', flightInfoMiddleware);



function flightInfoMiddleware($uibModal, FlightService) {

    let flightInfoMiddleware = this;
    flightInfoMiddleware.run = function (event) {
        FlightService.loadFlightConfiguration().then(function (currentFlightConfig) {
            if (!currentFlightConfig) {
                event.preventDefault();
                showFlightInformationEmptyModal($uibModal);
            }
        })
    };

    return {
        run: flightInfoMiddleware.run
    };
};

function showFlightInformationEmptyModal($uibModal) {
    const crewHomeState = 'component-crew.actions';
    const crewFlightConfigState = 'component-crew.settings-flight';
    const modalTitleText = "Flight Information Not Configured.";
    const modalBodyText = "Click on Ok to go to the Flight Information page.";
    const cancel = "Close";
    const proceedText = "Ok";

    return $uibModal.open({
        backdrop: 'static',
        component: "actionConfirmDialog",
        windowClass: 'popup-container',
        resolve: {
            modalText: () => modalBodyText,
            modalHeader: () => modalTitleText,
            modalLabelCancel: () => cancel,
            redirectState: () => crewFlightConfigState,
            modalLabelOk: () => proceedText,
            initialState: () => crewHomeState
        }
    });

}

angular.module('airfi.components.crew').controller('GalleyV2Controller', galleyV2Controller);

function galleyV2Controller($rootScope, $scope, $timeout, $q, DataService, GalleyService,
  AppConstants, CrewAirfilyticsService) {

  const vm = this;
  vm.orders = [];
  vm.expectations = null;
  vm.isOrderIdFromNotifications = false;
  vm.getOrderNo = getOrderNo;
  vm.updateOrderStatus = updateOrderStatus;
  vm.updateLineItemStatus = updateLineItemStatus;
  vm.shouldLineItemBeShownForTheStatus = shouldLineItemBeShownForTheStatus;
  vm.activate = activate;
  vm.isLineItemMatchingStatus = isLineItemMatchingStatus;
  vm.orderStatusLabels = AppConstants.ORDER_STATUS;
  vm.showButton = showButton;

  function activate() {
    DataService.fetchDocs('DeliveryExpectation').then(function (docs) {
      if (docs.rows.length > 0) {
        vm.expectations = docs.rows[0].doc;
      }
    });
    fetchOrders()
      .then(orders => {
        vm.orders = orders;
        $scope.$apply();
      });
    CrewAirfilyticsService.logCrewEvents('Entered Galley Section', {});
  }

  function fetchOrders() {
    return new Promise((resolve, reject) => {
      GalleyService.getAllOrders()
        .then(orders => {
           resolve(sortOrdersByTime(orders))
        }).catch(reject)
    });
  }

  function sortOrdersByTime(orders) {
    return orders.sort(function (x, y) {
      return new Date(y.timestamp) - new Date(x.timestamp);
    })
  }

  function shouldLineItemBeShownForTheStatus(lineItem, currentStatus) {
    if (AppConstants.ORDER_STATUS[currentStatus] === AppConstants.ORDER_STATUS.DEFAULT) {
      return true;
    }
    return lineItem.deliveryStatus === currentStatus;
  }

  function isLineItemMatchingStatus(order, statusToMatch) {
    return order.line_items && order.line_items.some(lineItem => {
      return lineItem.deliveryStatus === AppConstants.ORDER_STATUS[statusToMatch];
    });
  }

  function getOrderNo(orderId) {
    _.sortBy(vm.orders, 'timestamp');
    return (_.indexOf(vm.orders, _.find(vm.orders, {
      _id: orderId
    })) + 1);
  }

  function updateOrderStatus(order, lineItem, status) {
    order.status = status;
    order.isCrewNotified = true;
    order.line_items.forEach((item) => {
      item.deliveryStatus = status;
      const statusHistory = item.deliveryStatusHistory || [];
      statusHistory.push({
        status,
        timestamp: new Date().toISOString()
      })
      item.deliveryStatusHistory = statusHistory;
    });
    GalleyService.updateOrder(order);
  }

  function updateLineItemStatus(order, lineItem, status) {
    lineItem.deliveryStatus = status;
    const statusHistory = lineItem.deliveryStatusHistory || [];
    statusHistory.push({
      status,
      timestamp: new Date().toISOString()
    })
    lineItem.deliveryStatusHistory = statusHistory;
    // if all items in the order has the same status
    // the map will have one entry
    GalleyService.updateOrder(order);
    setTimeout($scope.$apply, 0)
  }

  function showButton(orderStatusKey) {
    return (orderStatusKey === vm.orderStatusLabels.APPROVED || orderStatusKey === vm.orderStatusLabels.PREPARING);
  }
}

angular
    .module('airfi.components.crew')
    .factory('StockService', stockService);

    function stockService($q, CrewServiceV2, UserService, DataService, ApiService) {
        let outOfStockProducts = [];
        const currentUser = UserService.getUserCredentials();

        return {
            fetchAllProducts,
            markProductOutOfStock,
            markProductInStock,
            getOutOfStockProducts,
            resetStockLevels,
        };

        function fetchAllProducts() {
            const def = $q.defer();
            DataService.fetchDocs('Product').then(function(docs) {
                def.resolve(docs);
            }, function(err) {
                def.reject(err);
            });
            return def.promise;
        }

        function markProductOutOfStock(productId) {
            const def = $q.defer();
            if (CrewServiceV2.isCrewUserLoggedIn()) {
                DataService.addDoc({
                    doc: {
                        _id: 'Stock/' + productId,
                        product_id: productId,
                        current_stock: 0,
                        type: 'Stock',
                    },
                    uid: currentUser.uid,
                    crewName: currentUser.name, //required for crew member authentication on server
                    crewSession: currentUser.crewSession, //required for crew member authentication on server
                }).then(() => {
                    def.resolve(productId);
                }, function (err) {
                  deferred.reject(err);
                });
            } else {
                def.reject();
            }
            return def.promise;
        }

        function markProductInStock(productId) {
            var def = $q.defer();
            if (CrewServiceV2.isCrewUserLoggedIn()) {
                const data = {
                    doc: {
                        _id: 'Stock/' + productId,
                        product_id: productId,
                        current_stock: 0,
                        type: 'Stock',
                    },
                    uid: currentUser.uid,
                    crewName: currentUser.name, //required for crew member authentication on server
                    crewSession: currentUser.crewSession, //required for crew member authentication on server
                }
                DataService.removeDoc(data).then(function () {
                    def.resolve();
                }, def.reject);
            } else {
                def.reject();
            }
            return def.promise;
        }

        function getOutOfStockProducts() {
            var def = $q.defer();
            ApiService.call('get', ApiService.routes.stockLevels())
                .then(function (stock) {
                    stock.data.rows.forEach(function (row) {
                        outOfStockProducts[row.doc.product_id] = true;
                    });
                    def.resolve(outOfStockProducts);
                }, def.reject)
            return def.promise;
        }

        function resetStockLevels() {
            var def = $q.defer();
            if (CrewServiceV2.isCrewUserLoggedIn()) {
                var data = {
                    doc: {
                        // when this document is processed on the server,
                        // all the docs in stock-level database with type Stock are
                        // removed from the stock-level database
                        _id: "ResetStock/" + new Date().toISOString(),
                        type: "ResetStock",
                    },
                    uid: currentUser.uid,
                    crewName: currentUser.name, //required for crew member authentication on server
                    crewSession: currentUser.crewSession, //required for crew member authentication on server
                };

                DataService.processDoc(data, function (err, docs) {
                    if(!err) {
                        def.resolve(docs);
                    } else {
                        def.reject(err);
                    }
                })
            }
            return def.promise;
        }
    }

'use strict';
angular.module('airfi.components.crew')
  .controller('CrewFlightSettingsController', crewFlightSettingsController);

function crewFlightSettingsController($rootScope, $scope, $q, $translate, FlightService, LocationService,
  CrewServiceV2, Notification, DataService, ApplicationService,LanguageService, AppConstants, CrewAirfilyticsService) {

  var vm = this;
  vm.viewedPublicAddress = false;
  vm.publicAddress = null;
  vm.isLoading = true;
  vm.airports = [];
  vm.errorResettingStock = false;
  vm.airfiVersion = null;
  vm.airfiCommitHash = null;
  vm.iata = null;
  vm.marketingCarrierCodes = null;
  vm.flightData = {
    originAirportName: null,
    flight_number: null,
    messageOfTheDay: null,
    arrivalAirportName: null,
    stopovers: [],
    tempUnit: null,
    currentLanguageCode: null,
    originAirportCode: null,
    arrivalAirportCode: null
  };
  vm.publicAddressDocs = [];
  vm.invaildOrigin = false;
  vm.invaildDestination = false;
  vm.isFlightConfigSaveInProgress = false;
  vm.marketingCarrierCodesEnabled = false; // default is false. 


  vm.activate = activate;
  vm.addStopover = addStopover;
  vm.removeStopover = removeStopover;
  vm.setAirportName = setAirportNameIntoFlightData;
  vm.setAircraftName = setAircraftName;
  vm.saveFlightConfig = saveFlightConfig;
  vm.selectOriginAirportCode = selectOriginAirportCode;
  vm.selectArrivalAirportCode = selectArrivalAirportCode;
  vm.stopoverAirportCode = stopoverAirportCode;
  const logoutListener = $rootScope.$on('crewLogout', function () {
    LocationService.goto('crew');
  });

  $scope.$on('$destroy', () => {
    logoutListener();
  });

  function selectOriginAirportCode(airport) {
    vm.flightData.originAirportName = airport.airportname;
  }

  function selectArrivalAirportCode(airport) {
    vm.flightData.arrivalAirportName = airport.airportname;
  }

  function stopoverAirportCode(airport, index) {
    vm.flightData.stopovers[index].airportName = airport.airportname;
  }

  function getAirportObjectFromAirportCode(airports = [], airportCode = '') {
    return airports.filter((airport) => {
      return airport.iata.toUpperCase() === airportCode.toUpperCase()
    });
  }

  function getMarketingCarrierCodes() {
    const getMarketingCarrierCodesSettings = ApplicationService.getMarketingCarrierCodes();
    const marketingCarrierCodesFromDB = getMarketingCarrierCodesSettings.marketingCarrierCodes;
    return marketingCarrierCodesFromDB;
  }

  function activate() {
    $q.all({
      initialize: CrewServiceV2.initialize(),
      airports: CrewServiceV2.getAirports(),
      aircrafts: CrewServiceV2.getAircrafts()

    }).then(function (result) {
      const { initialize: { flightConfig, iata }, airports, aircrafts } = result;
      const { stopovers } = flightConfig || {};
      vm.iata = iata;

      //  if marketing carrier code exists..There should be a default flight_number present(e.g AX-xxxx)
      // As backend rejects if flight_number is not present.
      //`moduleSettings/marketingCarrierCodes` has an array of marketingCarrierCodes.
      let marketingCarrierCodes = getMarketingCarrierCodes();
      if (marketingCarrierCodes) {
        vm.marketingCarrierCodesEnabled = true;
        vm.marketingCarrierCodes = marketingCarrierCodes;
        let defaultFlight_number = `${iata}xxxx`;
        vm.flightData = flightConfig || { flight_leg_identifier: { flight_number: defaultFlight_number } };
      } else {
        vm.marketingCarrierCodesEnabled = false;
        vm.flightData = flightConfig || { flight_leg_identifier: {} };
      }
      const { flight_leg_identifier: { origin, destination, flight_number } } = vm.flightData;
      vm.airports = airports || null;
      vm.aircrafts = aircrafts;
      vm.flightData.stopovers = stopovers || [];
      vm.flightData.originAirportCode = origin;
      vm.flightData.arrivalAirportCode = destination;
      vm.flightData.flight_number = flight_number && flight_number.split(iata)[1];
      vm.originAirportCode = origin || '';
      vm.arrivalAirportCode = destination || '';
      setAirportNameIntoFlightData();
      fetchFlightNumberList();
      vm.isLoading = false;
      CrewAirfilyticsService.logCrewEvents('Entered Flight Configuration', {});
      vm.mandatoryFlightConfigs = CrewServiceV2.getCrewActionConfiguration().additionalMandatoryFields;
      if(vm.mandatoryFlightConfigs && vm.mandatoryFlightConfigs.length){
        vm.mandatoryFlightConfigs.forEach((config) => {
          vm[config] = true;
        });
      }
    });

  }


  function setAirportNameIntoFlightData() {
    if (vm.airports.length) {
      const originAirport = getAirportObjectFromAirportCode(vm.airports, getOriginAirportCode(vm.flightData));
      const destinationAirport = getAirportObjectFromAirportCode(vm.airports, getDestinationAirportCode(vm.flightData));
      vm.flightData.originAirportName = (originAirport.length && originAirport[0].airportname) || '';
      vm.flightData.arrivalAirportName = (destinationAirport.length && destinationAirport[0].airportname) || '';
    }

  }

  function setAircraftName(tailNumber, clickAircraft) {
    vm.hideAircraftRegisterList = false;
    vm.aircraftregistrations = [];
    if (tailNumber) {
      vm.aircrafts.forEach(function (value) {
        for (var i = 0; i < value.registrations.length; i++) {
          if (value.registrations[i].indexOf(tailNumber.toUpperCase()) > -1) {
            vm.aircraftregistrations.push(value.registrations[i]);
            vm.flightData.aircraftRegisterType = tailNumber;
            vm.flightData.tailNumber = value.type;
            if (clickAircraft) {
              vm.hideAircraftRegisterList = !vm.hideAircraftRegisterList;
            }
          }
        }
      });
    } else {
      vm.flightData.tailNumber = '';
    }
  }

  function getOriginAirportCode(flightData) {
    const { originAirportCode } = flightData
    return originAirportCode && originAirportCode.toLowerCase();
  }
  function getDestinationAirportCode(flightData) {
    const { arrivalAirportCode } = flightData;
    return arrivalAirportCode && arrivalAirportCode.toLowerCase();
  }

  function checkAirportIsFromConfiguredList(selectedAirport, configureAirportList) {
    // if no AirportList is configured send true, as there is nothing to compare against
    if (!configureAirportList.length) {
      return true;
    }
    return getAirportObjectFromAirportCode(configureAirportList, selectedAirport).length

  }

  function checkAirportsValidity(flightData) {
    const airPortOrigin = getOriginAirportCode(flightData);
    const airPortDestination = getDestinationAirportCode(flightData);
    const isOriginFromTheListConfigured = !!checkAirportIsFromConfiguredList(airPortOrigin, vm.airports);
    const isDestinationFromTheListConfigured = !!checkAirportIsFromConfiguredList(airPortDestination, vm.airports);
    vm.invalidOrigin = vm.airlineForm.originAirportCode.$invalid || angular.isUndefined(airPortOrigin);
    vm.invalidDestination = vm.airlineForm.arrivalAirportCode.$invalid || angular.isUndefined(airPortDestination);
    return (!(vm.invalidOrigin && vm.invalidDestination)) &&
      isOriginFromTheListConfigured && isDestinationFromTheListConfigured;
  }

  function checkOriginAndDestinationNotSame(flightData) {
    const airPortOrigin = getOriginAirportCode(flightData)
    const airPortDestination = getDestinationAirportCode(flightData);
    return (airPortOrigin !== airPortDestination)
  }


  // check flight_number, marketingCarrierIATA, marketingFlightNumber is entered and matches the pattern..
  function validateFlightNumber(flightData) {
    if (vm.marketingCarrierCodesEnabled) {
      if (flightData.marketingFlightNumber && flightData.marketingCarrierIATA) {
        return validatePatternForFlightNumber(flightData.marketingFlightNumber);
      }
    }
    else if (flightData.flight_number) {
      return validatePatternForFlightNumber(flightData.flight_number);
    }
    return false;

  }

  function validatePatternForFlightNumber(flightCode) {
    let expectedPattern = new RegExp(/^[0-9]{2,4}$/);
    return expectedPattern.test(flightCode);
  }

  function saveFlightConfig() {
    const { flightData } = vm;
    vm.submitted = true
    //  form's data validation.. 
    if (vm.mandatoryFlightConfigs && vm.mandatoryFlightConfigs.length) {
      vm.formDataCheckSuccess = checkAirportsValidity(flightData) &&
        checkOriginAndDestinationNotSame(flightData) && validateFlightNumber(flightData) && validateRequiredFields();
    } else {
      vm.formDataCheckSuccess = checkAirportsValidity(flightData) &&
        checkOriginAndDestinationNotSame(flightData) && validateFlightNumber(flightData);
    }
      if (vm.formDataCheckSuccess) {
      vm.isFlightConfigSaveInProgress = true;
      vm.flightSaveSuccess = false;
      vm.flightSaveError = false;
      flightData.tempUnit = 'Â°C';
      flightData.currentLanguageCode = vm.currentLanguageCode;

      // add marketing_carrier_codes in flightData when it is present..
      if (vm.marketingCarrierCodesEnabled) {
        flightData.marketing_carrier_codes = vm.flightData.marketingCarrierIATA.concat(vm.flightData.marketingFlightNumber);
        flightData.flight_number = flightData.marketingFlightNumber;
      }

      flightData.currentLanguageCode = LanguageService.getCurrentLanguage();
      flightData.updatedBy = AppConstants.CREW;
      setAirportNameIntoFlightData();
      FlightService.addFlightConfig(flightData)
        .then(() => {
          vm.isFlightConfigSaveInProgress = false;
          Notification.success({
            message: $translate.instant('SETTINGS_SAVED_SUCCESSFULLY_MESSAGE'),
            closeOnClick: true,
            delay: 900
          });
          LocationService.goto('component-crew.actions');
          CrewAirfilyticsService.logCrewEvents('Submitted Flight Configuration', {flightData});
        }, () => {
          Notification.error({ message: $translate.instant('ERROR_SAVING_SETTINGS_MESSAGE'), closeOnClick: true });
        });
    } else {
      Notification.warning({ message: 'Check configuration for errors', closeOnClick: true });
    }
  }


  vm.loadAddress = function (id) { // Get the data of the public address selected by the user
    vm.viewedPublicAddress = true;
    vm.publicAddress = _.findWhere(vm.publicAddressDocs, {
      _id: id
    });
    vm.sendPublicAddressViewedEvent();
  };




  function addStopover() {
    vm.flightData.stopovers.push({
      airportCode: null,
      airportName: null
    });
  }


  function removeStopover(index) {
    vm.flightData.stopovers.splice(index, 1);
  }

  function fetchFlightNumberList() {
    const FLIGHT_NUMBER_LIST_ID = 'FlightNumberList';
    const deferred = $q.defer();
    DataService.fetchDocs(FLIGHT_NUMBER_LIST_ID).then(function (docs) {
      vm.flightNumberList = docs.rows.length && docs.rows[0].doc.FLIGHT_NUMBER_LIST;
      $timeout($scope.$apply, 0);
      deferred.resolve(vm.flightNumberList);
    }, deferred.reject);
    return deferred.promise;

  }

  function validateRequiredFields() {
    var validRequiredFields = 0;
    if (vm.mandatoryFlightConfigs && vm.mandatoryFlightConfigs.length) {
      vm.mandatoryFlightConfigs.forEach((i) => {
        if (document.getElementById(i).value) {
          validRequiredFields += 1;
        }
      })
    }
    if (validRequiredFields === vm.mandatoryFlightConfigs.length) {
      return true;
    } else {
      return false;
    }
  }

}

