angular
    .module('airfi.components')
    .factory('AnnouncementService', announcementService);

    function announcementService($uibModal, $rootScope, AppConstants, UserService, LocationService) {
        let modalInstance = null;
        return {
            startAnnouncement,
            openModal
        }

        function startAnnouncement (){
            const announcementStartEventListener = $rootScope.$on(
              AppConstants.BROADCASTED_EVENTS.ANNOUNCEMENT_STARTED, () => {
                if (!UserService.isCrewUser() && !modalInstance && !LocationService.isCrewLoginPage()) {
                  modalInstance = openModal();
                }
              });

            const announcementStopEventListener =  $rootScope.$on(
                AppConstants.BROADCASTED_EVENTS.ANNOUNCEMENT_STOPPED, () => {
                    if (modalInstance) {
                        modalInstance.close();
                        modalInstance = null;
                    }
                });

            $rootScope.$on('$destroy', announcementStartEventListener);
            $rootScope.$on('$destroy', announcementStopEventListener);
        }

        function openModal() {
            modalInstance =  $uibModal.open({
                backdrop: false,
                animation: true,
                windowClass: 'announcement-component',
                templateUrl: 'app/components/generic/announcement/announcement-modal.html',
                controller: 'AnnouncementModalController',
                controllerAs: 'vm',
                resolve: {
                    crewConfig: function(){
                        return LocationService.isCrewModule();
                    }
                }
            });
            return modalInstance;
        }

    }

