(function(){

  angular.module('airfi.components')
  .controller('CarouselAlphaController', CarouselAlphaController)

  function CarouselAlphaController ($rootScope, $window, $scope, LanguageService, AppConstants){
    const vm = this;
    vm.carouselInitalized = false;
    vm.$onInit = onInit;
    vm.getResponsiveImages = getResponsiveImages;
    vm.responsiveImages = [];
    vm.imageSizeSmall = false;

    const carouselSettings = {
      rewind: true,
      nav: false,
      slideSpeed: 2000,
      paginationSpeed: 300,
      items: 1,
      rtl: AppConstants.RTL_LANGUAGES.includes(LanguageService.getCurrentLanguage()),
      autoplay: true,
      lazyLoad: false,
      itemsDesktop: false,
      itemsDesktopSmall: false,
      itemsTablet: false,
      itemsMobile: false,
    };

    function onInit() {
      vm.carouselId = vm.componentConfig.id;
      refreshImageSize();
    }

    function refreshImageSize() {
      if ($window.innerWidth <= 460) {
        vm.imageSizeSmall = true;
      } else if ($window.innerWidth >= 461) {
        vm.imageSizeSmall = false;
      }
    }

    function getResponsiveImages(content, size) {
      let image = null;
      if (content && Object.keys(content).length > 0 && content.media.images) {
        image = content.media.images[size];
      }
      return image;
    }

    const onRepeatEvent = $rootScope.$on('onRepeatLast', (scope, element, attrs) => {
      if (vm.featuredData.length) {
        const owl = angular.element(`#${vm.carouselId}`)
        owl.on('initialized.owl.carousel', () => {
          vm.carouselInitalized = true;
        });
        owl.on('resize.owl.carousel', (scope, element, attrs) => {
          refreshImageSize();
          $scope.$apply();
        });
        owl.owlCarousel(carouselSettings);
      }
    });

    vm.$onDestroy = onRepeatEvent
  }
})();

