angular
    .module('airfi.components')
    .controller('MainHeaderController', mainHeaderController);


function mainHeaderController($rootScope, $state, $stateParams, $filter, $transitions, AirfilyticsService,
    LocationService, UserService, ApplicationService, ShopServiceV2, AppConstants, LanguageService) {
    const vm = this;
    vm.isCurrentUserCrew = false;
    vm.isHomePage = true;
    vm.gotoHomePage = gotoHomePage;
    vm.activate = activate;
    vm.logAirfilytics = logAirfilytics;
    vm.goToPreviousPage = goToPreviousPage;
    vm.isContentDetailPage = isContentDetailPage;
    vm.isCartPage = isCartPage;
    vm.isShopModule = isShopModule;
    vm.getTitle = getTitle;
    vm.goto = goto;
    vm.toggleMenu = toggleMenu;
    vm.isVideoDetailsPage = isVideoDetailsPage;
    vm.displayInteractiveMap = true;
    vm.gotoInteractiveMapPage = gotoInteractiveMapPage;

    const userLogoutEventListener = $rootScope.$on('LOGOUT_USER', gotoHomePage);
    const loginEventListener = $rootScope.$on('login', activate);
    const logoutEventListener = $rootScope.$on('logout', activate);
    const orderByFilter = $filter('orderElementBy');

    function handleUserLogin() {
        vm.userLoggedIn = true;
        vm.isCurrentUserCrew = UserService.isCrewUser();
    }

    function activate() {
        vm.menus = orderByFilter(ApplicationService.getMenuOptions(), { orderKey: 'sortOrder' });
        vm.isHomePage = LocationService.isHomePage();
        vm.headerConfig = ApplicationService.getHeaderConfig();
        vm.isCurrentUserCrew = UserService.isCrewUser();
        vm.showVideoTitle = ApplicationService.isDisplayShowVideoTitle();
        vm.contentModuleMappingDetails = ApplicationService.getContentModuleMappingDetails();
        if (UserService.isCrewLoggedIn() && UserService.isCrewUser()) {
            vm.userLoggedIn = true;
        }
        vm.displayInteractiveMap = ApplicationService.displayInteractiveMap();
    }

    function goto(config) {
        $state.go(config.name, config.options, { reload: false });
    }

    function gotoHomePage() {
        vm.userLoggedIn = false;
        LocationService.gotoHomePage();
    }

    function logAirfilytics(event, data) {
        AirfilyticsService.send(event, data);
    }

    function goToPreviousPage() {
        LocationService.goBack();
    }

    function isContentDetailPage() {
        var foundContentModuleMappingDetailsPage = Object.keys(vm.contentModuleMappingDetails).filter(function (key) {
            return Object.is(vm.contentModuleMappingDetails[key].subState, $stateParams.module);
        });
        return foundContentModuleMappingDetailsPage.length > 0 ? true : false;
    }

    function isVideoDetailsPage() {
        return $stateParams.module === 'video-play';
    }

    function gotoInteractiveMapPage() {
        const config = vm.displayInteractiveMap;
        $state.go(config.stateConfig.name, config.stateConfig.options, { reload: true });
    }

    function isCartPage() {
        return $state.current.isCartPage;
    }

    function isShopModule() {
        return $state.current.moduleName === 'shop' || $state.current.moduleName === 'cart';
    }

    function getTitle() {
        return LocationService.getPageTitle(vm.menus);
    }

    function toggleMenu() {
        $rootScope.$broadcast(AppConstants.BROADCASTED_EVENTS.MENU_TOGGLED);
    }

    const onVideoPage = $rootScope.$on(AppConstants.BROADCASTED_EVENTS.VIDEO_CONTENT_PAGE, function (e, title) {
        vm.videoTitle = title;
    });

    const crewLoginEvent = $rootScope.$on(AppConstants.BROADCASTED_EVENTS.CREW_LOGIN, handleUserLogin);

    $rootScope.$on('$destroy', () => {
        crewLoginEvent();
        onVideoPage();
        userLogoutEventListener();
        loginEventListener();
        logoutEventListener();
    });

    $transitions.onSuccess({
        to: true
    }, () => {
        vm.isHomePage = LocationService.isHomePage();
    });
}

