angular.module('airfi.components').controller('MusicPlayerController', musicPlayerController);

function musicPlayerController($filter, $rootScope, $scope, $state, $log,
  AppConstants, AirfilyticsService, Store, Actions) {
  const PLAY_BUTTON_CLASS = 'btn-play';
  const PAUSE_BUTTON_CLASS = 'btn-pause';
  const MINI_MUSIC_PLAYER_SLIDER_ID = '#mini-slider';
  const MUSIC_PLAYER_SLIDER_ID = '#slider';
  const MUSIC_HUB_STATE_NAME = 'music.hub';
  const DEFAULT_PLAYBACK_TIME = 300;
  const VIDEO_PLAYER_OPTIONS = {
    loop: false,
    controls: true,
    autoplay: true,
    preload: 'auto',
    playbackRates: [0.5, 1, 1.5, 2, 3],
  };

  let sliders = [];
  let wasMusicInPlayStateDuringAnnouncement = false;

  const vm = this;
  vm.audioPlayerOptions = VIDEO_PLAYER_OPTIONS;
  vm.media = {
    tracks: [],
    sources: [],
  };
  vm.songTiming = {
    timeEllapsed: 0,
    timeRemaning: 0,
  };

  vm.currentPlayingIndex = null;
  vm.currentlyPlayingTrackId = null;
  vm.$onInit = onInit;
  vm.getCurrentPlayList = getCurrentPlayList;
  vm.goToPreviousTrack = goToPreviousTrack;
  vm.goToNextTrack = goToNextTrack;
  vm.getCurrentlyPlayingSong = getCurrentlyPlayingSong;
  vm.getCurrentlyPlayingSongTitle = getCurrentlyPlayingSongTitle;
  vm.changeTrack = changeTrack;
  vm.playPause = playPause;
  vm.hidePlayer = hidePlayer;
  vm.closePlayer = closePlayer;
  vm.isMusicHubPage = isMusicHubPage;
  vm.hasPrevious = hasPrevious;
  vm.hasNext = hasNext;
  vm.moveUp = moveUp;
  vm.moveDown = moveDown;
  vm.deleteFromPlaylist = deleteFromPlaylist;
  vm.isShopModule = isShopModule;
  vm.$onChanges = onChanges;

  const gamePlayEventListener = $rootScope.$on(AppConstants.BROADCASTED_EVENTS.GAME_PLAY_PAGE_ENTERED, () => {
    vm.audioPlayer.pause();
  });

  const videoStartEventListener = $rootScope.$on(AppConstants.BROADCASTED_EVENTS.VIDEO_STARTED, () => {
    vm.audioPlayer.pause();
  });

  const playSongEventListener = $rootScope.$on(AppConstants.BROADCASTED_EVENTS.PLAY_SONG, (event, data) => {
    changeTrack(data.songId.toString());
  });

  const announcementStartedEventListener = $rootScope.$on(AppConstants.BROADCASTED_EVENTS.ANNOUNCEMENT_STARTED, () => {
    const isPlaying = !vm.audioPlayer.paused();
    if (vm.audioPlayer && isPlaying) {
      wasMusicInPlayStateDuringAnnouncement = true;
      vm.audioPlayer.pause();
      vm.hidePlayer();
    }
  });

  const announcementStoppedEventListener = $rootScope.$on(AppConstants.BROADCASTED_EVENTS.ANNOUNCEMENT_STOPPED, () => {
    if (wasMusicInPlayStateDuringAnnouncement) {
      wasMusicInPlayStateDuringAnnouncement = false;
      vm.audioPlayer.play();
    }
  });

  $rootScope.$on('$destroy', gamePlayEventListener);
  $rootScope.$on('$destroy', videoStartEventListener);
  $rootScope.$on('$destroy', playSongEventListener);
  $rootScope.$on('$destroy', announcementStartedEventListener);
  $rootScope.$on('$destroy', announcementStoppedEventListener);

  $scope.$on(AppConstants.MEDIA_PLAYER_EVENTS.READY, (event, data) => {
    vm.audioPlayer = data.player;
    vm.audioPlayer.volume(AppConstants.DEFAULT_VOLUME_LEVEL.DEFAULT_FOR_MUSIC);
    vm.audioPlayer.on(AppConstants.MEDIA_PLAYER_EVENTS.TIME_UPDATE, () => {
      vm.songTiming = {
        timeRemaning: vm.audioPlayer.remainingTime(),
        timeEllapsed: vm.audioPlayer.currentTime(),
      };
      sliders.forEach(slider => slider.setValue(vm.songTiming.timeEllapsed));
      $scope.$apply();
    });

    vm.audioPlayer.on(AppConstants.MEDIA_PLAYER_EVENTS.PLAY, () => {
      togglePlayPauseButton(PLAY_BUTTON_CLASS, PAUSE_BUTTON_CLASS);
    });

    vm.audioPlayer.on(AppConstants.MEDIA_PLAYER_EVENTS.FIRST_PLAY, () => {
      destroySliders();
      createSliders();
    });

    vm.audioPlayer.on(AppConstants.MEDIA_PLAYER_EVENTS.PAUSE, () => {
      togglePlayPauseButton(PAUSE_BUTTON_CLASS, PLAY_BUTTON_CLASS);
    });

    vm.audioPlayer.on(AppConstants.MEDIA_PLAYER_EVENTS.ERROR, (error) => {
      $log.log(error);
    });

    vm.audioPlayer.on(AppConstants.MEDIA_PLAYER_EVENTS.ENDED, () => {
      vm.goToNextTrack();
      $scope.$apply();
    });
  });

  function sliderDateFormatter(durationInSeconds) {
    const durationInMillis = durationInSeconds * 1000;
    return $filter('date')(durationInMillis, 'mm:ss', '+0000');
  }

  function onSlideEvent(currentSliderValue) {
    vm.audioPlayer.currentTime(currentSliderValue);
  }

  function isShopModule() {
    return $state.current.moduleName === 'shop' || $state.current.moduleName === 'cart';
  }

  function createSlider(sliderId) {
    const songDurationInSeconds = vm.audioPlayer.duration();
    const slider = new Slider(sliderId, {
      min: 0,
      max: isNaN(songDurationInSeconds) ? DEFAULT_PLAYBACK_TIME : songDurationInSeconds,
      formatter: sliderDateFormatter,
    });
    slider.on('slideStop', onSlideEvent);
    sliders.push(slider);
  }

  function createSliders() {
    createSlider(MINI_MUSIC_PLAYER_SLIDER_ID);
    createSlider(MUSIC_PLAYER_SLIDER_ID);
  }

  function destroySliders() {
    sliders.forEach(slider => slider.destroy());
    sliders = [];
  }

  function playPause() {
    if (vm.audioPlayer.paused()) {
      vm.audioPlayer.play();
    } else {
      vm.audioPlayer.pause();
    }
  }

  function getCurrentPlayList() {
    return vm.playlist;
  }

  function changeTrack(trackIndex) {
    Store.dispatch(Actions.MUSIC_PLAYER.CHANGE_TRACK, trackIndex);
    AirfilyticsService.sendMusicPlayedEvent(vm.playlist[vm.currentTrackIndex]);
  }

  function goToPreviousTrack() {
    if (hasPrevious()) {
      Store.dispatch(Actions.MUSIC_PLAYER.PREVIOUS_TRACK);
    }
  }

  function hasPrevious() {
    return vm.currentTrackIndex > 0
  }

  function hasNext() {
    return vm.currentTrackIndex < vm.playlist.length - 1;
  }

  function goToNextTrack() {
    if (hasNext()) {
      Store.dispatch(Actions.MUSIC_PLAYER.NEXT_TRACK);
    }
  }

  function onChanges() {
    if (vm.audioPlayer) {
      vm.audioPlayer.src({ src: vm.playlist[vm.currentTrackIndex].media.file, type: 'audio/mp3' });
    }
  }

  function deleteFromPlaylist(trackIndex) {
    Store.dispatch(Actions.MUSIC_PLAYER.DELETE_TRACK, trackIndex);
    if (trackIndex === vm.currentTrackIndex) {
      onChanges();
    }
  }

  function moveDown(trackIndex) {
    Store.dispatch(Actions.MUSIC_PLAYER.MOVE_DOWN_IN_PLAYLIST, trackIndex);
  }

  function moveUp(trackIndex) {
    Store.dispatch(Actions.MUSIC_PLAYER.MOVE_UP_IN_PLAYLIST, trackIndex);
  }

  function getCurrentlyPlayingSong() {
    return vm.playlist[vm.currentTrackIndex];
  }

  function getCurrentlyPlayingSongTitle() {
    return vm.playlist[vm.currentTrackIndex].labels.title;
  }

  function hidePlayer() {
    dimClickHandler();
  }

  function closePlayer() {
    destroySliders();
    vm.audioPlayer.dispose();
    Store.dispatch(Actions.MUSIC_PLAYER.CLOSE_PLAYER);
  }

  function isMusicHubPage() {
    return $state.current.name === MUSIC_HUB_STATE_NAME;
  }

  function togglePlayPauseButton(buttonToHide, buttonToShow) {
    TweenMax.to(angular.element(`.${buttonToHide}`), 0.2, {
      x: 20,
      opacity: 0,
      scale: 0.3,
      display: 'none',
      ease: Power2.easeInOut,
    });
    TweenMax.fromTo(angular.element(`.${buttonToShow}`), 0.2, {
      x: -20,
      opacity: 0,
      scale: 0.3,
      display: 'none',
    }, {
        x: 0,
        opacity: 1,
        scale: 1,
        display: 'block',
        ease: Power2.easeInOut,
      });
  }

  function dimClickHandler() {
    $rootScope.$broadcast(AppConstants.BROADCASTED_EVENTS.MUSIC_PLAYER_TOGGLED);
    TweenMax.to('.dim', 0.5, {
      opacity: 0,
      display: 'none',
      ease: Power2.easeInOut,
    });
    TweenMax.to('#player', 0.5, {
      xPercent: 100,
      display: 'none',
      ease: Expo.easeOut,
    });
    TweenMax.to('.nav', 0.5, {
      xPercent: -100,
      display: 'none',
      ease: Power2.easeInOut,
    });
    TweenMax.to('.mini-player', 0.5, {
      x: 0,
      ease: Expo.easeOut,
    });
  }

  function onInit() {
    vm.media.sources = [
      { src: vm.playlist[vm.currentTrackIndex].media.file, type: 'audio/mp3' }
    ];

    // ===== Open Player + dim on =====
    angular.element('.btn-open-player, .track_info').click(() => {
      $rootScope.$broadcast(AppConstants.BROADCASTED_EVENTS.MUSIC_PLAYER_TOGGLED);
      TweenMax.to('.dim', 0.5, {
        opacity: 1,
        display: 'block',
        ease: Power2.easeInOut,
      });
      TweenMax.fromTo('#player', 0.5, {
        xPercent: 100,
      }, {
          xPercent: 0,
          display: 'block',
          ease: Expo.easeOut,
        });
      TweenMax.to('.mini-player', 0.5, {
        x: 1000,
        ease: Expo.easeOut,
      });
    });

    angular.element('.dim').click(dimClickHandler);

    // ===== HoverIn/HoverOut Flash Effect =====

    angular.element('.track_info').hover(() => {
      TweenMax.fromTo(angular.element(this), 0.5, {
        opacity: 0.5,
        ease: Power2.easeInOut,
      }, {
          opacity: 1,
        });
    },
      () => {
        angular.element(this).css('opacity', '1');
      });

    angular.element('.burger-wrapper, .logo-text, .back_btn').hover(() => {
      TweenMax.fromTo(angular.element(this), 0.5, {
        opacity: 0.5,
        ease: Power2.easeInOut,
      }, {
          opacity: 1,
        });
    },
      () => {
        angular.element(this).css('opacity', '1');
      });

    angular.element('.btn-open-player').hover(() => {
      TweenMax.fromTo(angular.element(this), 0.5, {
        opacity: 0.5,
        ease: Power2.easeInOut,
      }, {
          opacity: 1,
        });
    },
      () => {
        angular.element(this).css('opacity', '1');
      });

    angular.element('.nav a').hover(() => {
      TweenMax.fromTo(angular.element(this), 0.5, {
        opacity: 0.5,
        ease: Power2.easeInOut,
      }, {
          opacity: 1,
        });
    },
      () => {
        angular.element(this).css('opacity', '1');
      });

    // ===== Player - List Items =====
    angular.element('.list_item').click(() => {
      angular.element('.list_item').removeClass('selected');
      angular.element(this).addClass('selected');
    });


    // ===== Main Play Button - Hover =====

    angular.element('.text-wrap .text').hover(() => {
      TweenMax.to(angular.element('.main-btn_wrapper'), 0.5, {
        opacity: 1,
        display: 'block',
        position: 'absolute',
        scale: 1,
        ease: Elastic.easeOut.config(1, 0.75),
      });

      TweenMax.to(angular.element('.line'), 0.5, {
        css: {
          scaleY: 0.6,
          transformOrigin: 'center center',
        },
        ease: Expo.easeOut,
      });
    },
      () => {
        TweenMax.to(angular.element('.main-btn_wrapper'), 0.5, {
          opacity: 0,
          display: 'none',
          scale: 0,
          ease: Elastic.easeOut.config(1, 0.75),
        });

        TweenMax.to(angular.element('.line'), 0.5, {
          css: {
            scaleY: 1,
            transformOrigin: 'center center',
          },
          ease: Expo.easeOut,
        });
      });
  }
}

