angular
  .module('airfi.components')
  .controller('PremiumContentSignUpController', function premiumContentIntroController(
        $rootScope, $scope, $uibModal, $uibModalInstance, UserService,
        PremiumContentService, PremiumContentAirfilyticsService, AppConstants) {
    const vm = this;

    vm.init = init;
    vm.close = close;
    vm.signUpForPremiumCodeAccess = signUpForPremiumCodeAccess;
    vm.premiumContentAccessError = false;

    vm.user = { name: '', seatNumber: '' };
    vm.contentDefaultPrice = '';
    vm.voucherAccessSettings = null;

    const EVENT_USER_PREMIUM_CONTENT_ACCESS_VALIDATED = 'EVENT_USER_PREMIUM_CONTENT_ACCESS_VALIDATED';
    let identifiedUserDetails = null;

    function init() {
      identifiedUserDetails = UserService.isUserIdentified();
      if (identifiedUserDetails) {
        const user = UserService.getUser();
        vm.user.name = user.name;
        vm.user.seatNumber = user.seat;
      }
    }


    function close() {
      $uibModalInstance.dismiss('cancel');
    }


    function signUpForPremiumCodeAccess() {
      if (vm.contentAccessSignupForm.$valid) {
        if (!identifiedUserDetails) {
          const userInputs = {
            name: vm.user.name,
            seat: vm.user.seatNumber.toUpperCase()
          }
          UserService.updateUserDetails(userInputs)
            .then(() => {
              checkPremiumContentAccessCode();
            });
        } else {
          checkPremiumContentAccessCode();
        }
      } else {
        vm.signFormInvlid = 'Error identifying with AirFi. Please try again later.';
      }
    }

    function checkPremiumContentAccessCode() {
      if (vm.user.voucherCode) {
        const voucherCode = vm.user.voucherCode.toUpperCase();
