angular
  .module('airfi.components')
  .controller('RatingController', ratingController);

  function ratingController($window, $timeout, $uibModal, ApplicationService, UserService, $rootScope, AppConstants, 
    VideoService) {
  const vm = this;
  vm.$onInit = onInit;
  vm.hideRatingPopup = false;
  vm.isGamePlaying = false;
  vm.ratingConfig = ApplicationService.getStarRatingConfig();

  function onInit() {
    if (vm.ratingConfig && vm.ratingConfig.alertTimeout && !$window.sessionStorage.doNotDisplayRatingAgain &&
      vm.ratingConfig.formName && vm.ratingConfig.provider ) {
      $timeout(showRatingPopup, (vm.ratingConfig.alertTimeout * 1000));
    }
  }

  function showRatingPopupLater() {
    if (vm.ratingConfig.reminderTimeout) {
      $timeout(() => {
        showRatingPopup();
      }, vm.ratingConfig.reminderTimeout * 1000);
    }
  }

  function showRatingPopup() {
    if(vm.ratingConfig.doNotShowRatingPopup){
      vm.hideRatingPopup = VideoService.isVideoPlaying();
    }
    if(vm.ratingConfig.notShowRatingWhileGamePlay){
      vm.isGamePlaying = String(sessionStorage.getItem('isGamePlaying')) == 'true';
    }
    if (!UserService.isCrewLoggedIn() && !vm.hideRatingPopup && !vm.isGamePlaying) {
      $uibModal.open({
        backdrop: 'static',
        templateUrl: 'app/components/generic/rating/star-rating.html',
        controller: 'StartRatingController',
        controllerAs: 'vm'
      });
      $rootScope.$broadcast(AppConstants.BROADCASTED_EVENTS.RATINGS_POPUP_DISPLAYED);
    } else {
      showRatingPopupLater();
    }
  }
}

