angular
  .module('airfi.components')
  .controller('SecondaryMenuV2Controller', secondaryMenuV2Controller);

function secondaryMenuV2Controller($scope, $rootScope, $stateParams, $transitions,
  ApplicationService, LocationService, AppConstants) {
  const vm = this;
  const hamburgerMenuStateEvent = $rootScope.$on(AppConstants.MENU_CHANGE_EVENT, hamburgerMenuState);
  vm.$onInit = initialize;
  vm.isHomePage = true;
  vm.isShopState = false;
  vm.isNewShopState = false;
  vm.goToPreviousPage = goToPreviousPage;
  vm.isContentDetailPage = isContentDetailPage;
  vm.enableActivate = enableActivate;
  vm.goto = goto;
  vm.isNewsState = false;

  function initialize() {

    vm.secondaryMenu = ApplicationService.getSecondaryMenu();
    vm.isHomePage = LocationService.isHomePage();
    vm.isShopState = LocationService.isShopState();
    vm.selectedmenu = $stateParams.module;
    vm.contentModuleMappingDetails = ApplicationService.getContentModuleMappingDetails();
  }

  function goToPreviousPage() {
    if (!isContentDetailPage() && !vm.isNewShopState && !vm.isNewsState) {
      LocationService.gotoHomePage();
    } else {
      LocationService.goBack();
    }
  }

  function isContentDetailPage() {
    var contentModuleMappingDetailsPage = Object.keys(vm.contentModuleMappingDetails).filter(function (key) {
      return Object.is(vm.contentModuleMappingDetails[key].subState, $stateParams.module);
    });

    return contentModuleMappingDetailsPage.length > 0 ? true : false;
  }

  function goto(config) {
    if (config.options.module) {
      vm.selectedmenu = config.options.module;
      LocationService.gotoV2(config.name, config.options, false)
    } else {
      LocationService.redirectToURL(config.options.redirectUrl);
    }

  }
  $scope.$on('$destroy', () => {
    hamburgerMenuStateEvent();
  });

  function hamburgerMenuState(event, state) {
    vm.selectedmenu = state.module;
  }

  function enableActivate (options) {
    return (options.id ? options.module === vm.selectedmenu && options.id === vm.selectedmenuId : options.module === vm.selectedmenu);
  }

  $transitions.onSuccess({
    to: true
  }, () => {
    vm.isHomePage = LocationService.isHomePage();
    vm.isShopState = LocationService.isShopState();
    vm.selectedmenu = $stateParams.module;
    vm.selectedmenuId = $stateParams.id;
    vm.isNewsState = LocationService.isNewsState();
    vm.isNewShopState = LocationService.isNewShopState($stateParams.module)
  });

}

