angular.module('airfi.components')
.controller('SeriesGalleryController', seriesGalleryController);

function seriesGalleryController($stateParams, $scope, $timeout, AppConstants, LocationService) {

  const vm = this;
  const AUTOPLAY_EVENT = AppConstants.BROADCASTED_EVENTS.AUTOPLAY_TRIGGERED;
  const IMAGE_CONTENT_TYPE = "image";

  vm.category = null;
  vm.filterRule = AppConstants.DATA_FILTER_RULE;
  vm.video = null;
  vm.seriesTitle = '';
  vm.seriesDescription = '';
  vm.episodeListWidget = angular.element('#episode-changer-carousel');
  vm.videoPlayerOptions = AppConstants.VIDEO_PLAYER_OPTIONS;
  vm.clips = [];
  vm.channel = '';

  vm.$onInit = onInit;
  vm.goBack = LocationService.goBack;
  vm.toggleAutoplay = true;
  vm.autoPlayText = AppConstants.SERIES_PLAY_AUTOPLAY.ON

  window.addEventListener(AUTOPLAY_EVENT, (data) => {
    const {labels:{ title } = {} } = data.detail || {}
    vm.clipTitle = title
  })

  function onInit() {
    const { hasClips, hasFullEpisodes, autoplay, autoplayNextVideo } = vm.settings;
    vm.toggleAutoplay = autoplayNextVideo;
    vm.autoPlayText = autoplayNextVideo ? AppConstants.SERIES_PLAY_AUTOPLAY.ON : AppConstants.SERIES_PLAY_AUTOPLAY.OFF;
    vm.videoPlayerOptions.autoplay = autoplay;
    vm.category = $stateParams.series;
    vm.mainImage = getImageData($stateParams.id);

    const { labels: { title, longDescription } = {} } = vm.mainImage || {}
    vm.seriesTitle = title;
    vm.seriesDescription = longDescription;

    const { media: { images } = {}, provider } = vm.mainImage || {}
    vm.seriesPoster = images.default;
    vm.channel = provider;

    if (hasFullEpisodes)
      prepareEpisodes();

    if (hasClips) {
      const { labels: { shortDescription: { episodes } = {} } = {} } = vm.mainImage || {}
      vm.episodeNumbers = episodes;
      vm.currentEpisode = vm.episodeNumbers[vm.episodeNumbers.length - 1];
      prepareClips();
    }

  }

  function prepareEpisodes() {
    vm.clips = getVideos(vm.category);
    vm.video = vm.clips[0];
    vm.clipTitle = '';
    vm.episodeNumbers = vm.getNumber(vm.clips.length);
    vm.currentEpisode = vm.episodeNumbers[vm.episodeNumbers.length - 1];
  }

  function prepareClips() {
    vm.clips = getVideos(`${vm.category}${vm.currentEpisode}`);
    vm.video = vm.clips[0];
    vm.clipTitle = vm.clips[0].labels.title;
  }

  vm.initializeEpisodeListWidget = function () {
    vm.episodeListWidget.owlCarousel({
      center: true,
      loop: false,
      dots: false,
      margin: 10,
      startPosition: vm.episodeNumbers.length - 1,
      nav: true,
      rtl: false,
      items: 7,
      navText: ['<i class=\'fa fa-arrow-left\'></i>', '<i class=\'fa fa-arrow-right\'></i>'],
    });
    vm.episodeListWidget.on('changed.owl.carousel', changeEpisode);
  };

  function getVideos(category) {
    const filteredVideos = vm.contentList.filter(
      content => (content.type === AppConstants.CONTENT_TYPES.VIDEO && content.category.id === category)
    );
    return filteredVideos.reverse();
  }

  function getImageData(id) {
    const filteredList = vm.contentList.filter(content => (content.type === IMAGE_CONTENT_TYPE && content.id === id));
    return filteredList.length ? filteredList[0] : null;
  }

  vm.getNumber = function (num) {
    const numbers = Array.from(Array(num + 1).keys());
    numbers.splice(0, 1);
    return numbers;
  };

  function changeEpisode(event) {
    const { hasClips, hasFullEpisodes } = vm.settings;

    const { item: { index } = {} } = event || {}
    const episodeIndex = index
    vm.currentEpisode = vm.episodeNumbers[episodeIndex];
    if (hasClips)
      prepareClips();

    if (hasFullEpisodes) {
      vm.video = vm.clips[episodeIndex];
      vm.clipTitle = '';
    }
    // $apply is to digest the changes made to scope as angularjs is stupid on its own.
    // $timeout to avoid consecutive $apply
    $timeout($scope.$apply, 0);
  }

  vm.setEpisode = function (episodeIndex) {
    vm.toggleAutoplay = vm.settings.autoplayNextVideo;
    vm.autoPlayText = vm.toggleAutoplay ? AppConstants.SERIES_PLAY_AUTOPLAY.ON : AppConstants.SERIES_PLAY_AUTOPLAY.OFF;
    vm.episodeListWidget.trigger('to.owl.carousel', [episodeIndex, 100]);
  }

  vm.playClip = function (content) {
    vm.video = content;
    vm.toggleAutoplay = false;
    vm.autoPlayText = AppConstants.SERIES_PLAY_AUTOPLAY.OFF;

    const { labels: { title } = {} } = content || {}
    vm.clipTitle = title

    angular.element('html, body').animate({
      scrollTop: 0
    }, 600);
  };

  vm.autoPlay = function () {
    vm.toggleAutoplay = !vm.toggleAutoplay;
    vm.autoPlayText = vm.toggleAutoplay ? AppConstants.SERIES_PLAY_AUTOPLAY.ON : AppConstants.SERIES_PLAY_AUTOPLAY.OFF;
  };
}
})()

angular
  .module('airfi.components')
  .controller('StaticController', staticController)

function staticController(LanguageService, ContentServiceV3, UtilService, ApplicationService, AppConstants) {
  const vm = this;
  vm.$onInit = updateExternalResourceUrl;
  const v1LanguageCodes = ApplicationService.getV1LanguageCodes();

  function updateExternalResourceUrl() {
    const currentLanguage = LanguageService.getCurrentLanguage();
    const overrideLanguage = vm.componentConfig.overrideLanguage;
    vm.isFullScreen = vm.componentConfig.id === 'interactive_map-component';
    generateExternalContentUrl(currentLanguage).then(url => {
        return url;
      })
      .then(url => checkIfResourceExists(url))
      .catch(() => {
        generateExternalContentUrl(v1LanguageCodes[currentLanguage]).then(url => {
          return url;
          })
          .then(url => checkIfResourceExists(url))
          .catch(() => {
            generateExternalContentUrl(overrideLanguage).then(url => {
                return url;
              })
              .then(url => checkIfResourceExists(url))
              .catch(() => {
                generateExternalContentUrl(v1LanguageCodes[overrideLanguage]).then(url => {
                  vm.externalResourceUrl = url;
                });
              })
          });
      });
  }

  function checkIfResourceExists(url) {
    return UtilService.isResourceExist(url).then(() => {
      vm.externalResourceUrl = url;
    });
  }

  function generateExternalContentUrl(languageCode) {
    return new Promise((resolve, reject) => {
      const params = vm.componentConfig.parameters;
      ContentServiceV3.getContentById(vm.componentConfig.type, vm.componentConfig.moduleName).then(content => {

        if (content && content.data && content.data.type && content.data.type === AppConstants.COMPONENT_TYPE.STATIC) {
          resolve(ContentServiceV3.createStaticUrlForManifestV3(
            content.data.media.root,
            languageCode));
        } else {
          const url = ContentServiceV3.createUrl(
            vm.componentConfig.provider,
            vm.componentConfig.moduleName,
            languageCode,
            vm.componentConfig.path);
          resolve(params ? url.concat(`?${params}`) : url);
        }
      }).catch(err => {
        const url = ContentServiceV3.createUrl(
          vm.componentConfig.provider,
          vm.componentConfig.moduleName,
          languageCode,
          vm.componentConfig.path);
        resolve(params ? url.concat(`?${params}`) : url);

      })
    })
  }
}

