angular
  .module('airfi.components.shop')
  .controller('ShopOrderFulfilmentController', shopOrderFulfilmentController)

function shopOrderFulfilmentController(
  $stateParams,
  $state,
  UserService,
  ShopServiceV2,
  ShopAirfilyticsServiceV2,
  LocationService,
  PremiumContentAirfilyticsService,
  AppConstants) {
  const vm = this;
  vm.order = null;
  vm.orderInfo = {};
  vm.cartProducts = null;
  vm.initialize = initialize;
  vm.goToShop = goToShop;
  vm.billingDescriptor = null;

  const ORDER_SUCCESS_STATUS = 'SUCCESS';


  function initialize() {
    const paymentId = $stateParams.paymentId;
    ShopServiceV2.initialize()
      .then((data) => {
        const paymentMethod = data.cart.paymentMethod;
        if (!data.cart.lineItems.length) {
          goToShop();
        } else if (paymentMethod) {
          convertCartToOrder(paymentMethod, paymentId, data.cart.uid)
        } else {
          goToDeliveryAndPaymentDetailsPage();
        }
      });
  }

  function convertCartToOrder(paymentMethod, paymentId, uuid) {
    ShopServiceV2.convertCartToOrder(paymentMethod, paymentId, uuid)
      .then((response) => {
        if (response.data && response.data.order) {
          if (response.data.order.order_identifier) {
            const orderId = response.data.order.order_identifier.split('-');
            vm.orderInfo.orderId = orderId[orderId.length - 1];
          }
          if (response.data.order.timestamp) {
            vm.orderInfo.orderTimestamp = new Date(response.data.order.timestamp).toString();
          }
        }
        const result = response.data;
        if (result.status === ORDER_SUCCESS_STATUS) {
          orderCompleteSuccess(result);
        } else {
          vm.errorMessage = result.message;
          ShopAirfilyticsServiceV2.orderCompleteError(result.message)
        }
      });
  }

  function orderCompleteSuccess(result) {
    ShopAirfilyticsServiceV2.orderCompleteSuccess(result.order);
    ShopServiceV2.clearCart();
    vm.order = result.order;
    vm.selectedCurrency = vm.order.transaction_currency;
    vm.message = result.message;
    vm.cartProducts = vm.order.line_items;
    vm.grandTotal = ShopServiceV2.calculateCartTotal(vm.order.line_items, vm.order.transaction_currency)
    vm.totalSaving = ShopServiceV2.calculateTotalSavings(vm.order.line_items, vm.order.transaction_currency);
    ShopServiceV2.bubbleCartHasProductsEvent(vm.order.line_items, vm.order.transaction_currency);
    vm.order.line_items.forEach((item) => {
      if (item.product.productType === AppConstants.PRODUCT_TYPE_VOUCHER && angular.isDefined(item.product.accessCode)) {
        $state.go(LocationService.getCachedRedirectState().name,
            LocationService.getCachedRedirectState().options)
          .then(() => {
            PremiumContentAirfilyticsService.premiumContentAccessActivated('Purchase');
          });
      }
    });
  }

  function goToDeliveryAndPaymentDetailsPage() {
    ShopServiceV2.getShoppingDeliveryPaymentDetailsStateConfig().then(stateConfig => {
      LocationService.gotoV2(stateConfig.name, stateConfig.options, false);
    });
  }

  function goToShop() {
    const stateConfig = vm.componentConfig.stateConfig;
    LocationService.gotoV2(stateConfig.name, stateConfig.options, false);
  }
}

