function passengerDetailsController($rootScope, $stateParams, AppConstants, UserService, ShopServiceV2) {
  const vm = this;
  vm.initialize = initialize;
  vm.$onDestroy = onDestroy;
  vm.user = null;
  vm.isUserDetailsPresent = false;
  vm.userDetailsMissingError = false;

  function onDestroy() {
    passengerDetailsSubmittedEventListener();
  }

  function initialize() {
    vm.isUserDetailsPresent = false;
    vm.user = UserService.getUser();
    ShopServiceV2.initialize()
      .then((data) => {
        if (data.cart.userName) {
          vm.user.name = data.cart.userName;
          vm.user.seat = data.cart.seatNumber;
          const currentModule = $stateParams.module;
          if (currentModule === AppConstants.SHOP_ROUTE_NAMES.ORDER_FULFILMENT) {
            vm.isUserDetailsPresent = true;
          }
        }
      });
  }

  const passengerDetailsSubmittedEventListener = $rootScope.$on(AppConstants.SHOP.PASSENGER_DETAILS_SUBMITTED,
    () => {
        vm.userDetailsMissingError = true;
        const userInputs = {
          name: getPassengerName(),
          seat: getPassengerSeat()
        }
        UserService.updateUserDetails(userInputs).then((user) => {
          $rootScope.$emit(AppConstants.SHOP.PASSENGER_DETAILS_UPDATED, user);
        });
    });

  function getPassengerName() {
    return vm.user.name;
  }

  function getPassengerSeat() {
    return vm.user.seat.toUpperCase();
  }

  $rootScope.$on('$destroy', () => {
    passengerDetailsSubmittedEventListener();
  });
}

