function priceController(PromotionsService, ShopServiceV2, ApplicationService, ApiService) {
  const vm = this;
  vm.price = null;
  vm.localizedCurrency = null;
  vm.localizedCurrencyPrice = null;
  vm.salePercentage = null;
  vm.salePrice = null;
  vm.initialize = initialize;
  vm.selectedCurrency = null;
  vm.init = init;

  function initialize() {
    ApiService.call('get', ApiService.routes.availableCurrencies(), {
        cache: true
      })
      .then((response) => {
        const result = response.data;
        const selectedCurrency = ShopServiceV2.getSelectedCurrency() || result.base;
        vm.init(selectedCurrency)
      });
  }

  function init(currency) {
    vm.selectedCurrency = currency;
    PromotionsService.getPromotionsDetails().then(promotionInfo => {
      vm.localizedCurrency = ApplicationService.getLocalizedCurrency();
      PromotionsService.applyPromotions(vm.content, vm.selectedCurrency, vm.localizedCurrency);
      const {
        promotionPriceInfo,
        pricing
      } = vm.content;
      vm.localizedCurrencyPrice = _.get(promotionPriceInfo, 'localizedSalePrice',
        pricing.commercialPrice[vm.localizedCurrency.currency]);
      vm.price = _.get(promotionPriceInfo, 'price', pricing.commercialPrice[vm.selectedCurrency.currency]);
      vm.salePrice = _.get(promotionPriceInfo, 'salePrice', vm.price);
      vm.salePercentage = _.get(promotionPriceInfo, 'salePercentage', null);
    });
  }
}

