/**
 * @ngdoc directive
 * @name airfiApp.directive:allowZoom
 * @description
 * # allowZoom: Allows/disallows a view to be zoomed by the user
 */
angular.module('airfiApp')
    .directive('allowZoom', [function () {
        return {
            restrict: 'A',
            link: function postLink(scope, element, attrs) {
                // The first meta viewport tag on the page is the one we consider
                var zoomTag = $('#zoom-viewport');
                // Store it's current value, we will restore it when we navigate away from this view
                var originalZoomSettings = $(zoomTag).attr('content');

                // Does the page disallow zooming?
                if (attrs.allowZoom === 'no') {
                    zoomTag.attr('content', 'maximum-scale=1, user-scalable=0, initial-scale=1');
                } else if (attrs.allowZoom === 'yes') { // or does it allow zooming?
                    zoomTag.attr('content', 'maximum-scale=2.2, user-scalable=1, initial-scale=1');
                }

                // When navigating away from the view, restore the original zoom settings
                scope.$on('$routeChangeStart', function(next, current) {
                    zoomTag.attr('content', originalZoomSettings);
                });
            }
        };
    }]);

