angular.module('airfiApp')
  .directive('resizeIframe', function ($timeout) {
    return {
      restrict: 'A',
      scope: {
        iframeSrc: '@'
      },
      link: function postLink(scope, element, attrs) {

        var destroySrcWatch = null;
        var adjustIframe = function () {
          element.height("100vh");
        };

        function loadContent() {
          if (scope.iframeSrc) {
            element.attr('src', scope.iframeSrc);
          }

          destroySrcWatch = scope.$watch('iframeSrc', function (newSrc) {
            if (newSrc) {
              $timeout(function () {
                element.attr('src', newSrc);
              });
            }
          });
        }

        $(document).ready(function () {
          $timeout(function () {
            adjustIframe();
            loadContent();
          }, 100);
        });

        $(window).resize(function () {
          adjustIframe();
        });

        //clean up listeners and make the body scrollable again
        scope.$on('$destroy', function () {
          $('body').css('overflow-y', 'visible');
          adjustIframe = function () {};
          if (destroySrcWatch) {
            destroySrcWatch();
          }
        });
      }
    };
  });

