/**
 * @ngdoc filter
 * @name airfiApp.filter:orderByLineItemStatus
 * @function
 * @description
 * # orderByLineItemStatus
 * Filter in the airfiApp.
 */
angular.module('airfi.platform.filters')
  .filter('orderByLineItemsStatus', function orderByLineItemsStatus() {
    return function (orders, status) {
      const filteredOrders = [];
      for (let i = 0; i < orders.length; i += 1) {
        if (orders[i].line_items) {
          for (let j = 0; j < orders[i].line_items.length; j += 1) {
            if (orders[i].line_items[j].delivery_status === status) {
              filteredOrders.push(orders[i]);
              break;
            }
          }
        }
      }
      return filteredOrders;
    };
});

/**
 * @ngdoc filter
 * @name airfiApp.filter:trimPrecedingZeros
 * @function
 * @description
 * # trimPrecedingZeros
 * Filter in the airfiApp.
 */
angular.module('airfi.platform.filters')
  .filter('trimPrecedingZeros', function filter() {
    return function (input) {
      let inputVal = input;

      if (inputVal) {
        // ^ matches on the start of the string.
        // (-)? matches an optional - this will be reinserted in the replacement string.
        // (0+)(?=\d) matches a series of 0 with a digit following.
        // (?=\d) is a lookahead assertion, it does not match but ensure that a
        // digit is following the leading zeros.
        const trimPrecedingZeros = /^(-)?0+(?=\d)/;

        inputVal = inputVal.replace(trimPrecedingZeros, '$1');
      }

      return inputVal;
    };
});

