function getMockedStore() {
  const { distinctUntilChanged, map } = rxjs.operators;
  const initialEmptyState = {};
  const store = new rxjs.BehaviorSubject(initialEmptyState);

  return {
    select,
    dispatch,
    setState
  };

  function select(selector) {
    return store.pipe(
      distinctUntilChanged((previous, current) => selector &&
        _.isEqual(_.get(previous, selector, undefined), _.get(current, selector, undefined))),
      map((state) => selector ? _.get(state, selector) : state),
    )
  }

  function setState(state){
    store.next(state);
  }

  function dispatch(action, payload) {}
}

angular.module('airfi.platform.dynamic').controller('DynamicController', dynamicController);


function dynamicController($state, $stateParams, $rootScope, $location, $filter, $q,
  ContentServiceV3, AppConstants, ApplicationService, PremiumContentService, AirfilyticsService,
  UtilService, Store, Actions, LanguageService, ApiService, CrewServiceV2, AnnouncementService, ContentApiService,
  LocationService, CustomFilterService, $window, $log) {
  const vm = this;
  let content = {};
  const SUPPORTED_FILTER_PARAMS = ['provider', 'category', 'tags'];
  const HTTP_METHODS = AppConstants.HTTP_METHODS;
  const CONTENT_TYPES = AppConstants.CONTENT_TYPES;
  const limitToFilter = $filter('limitTo');
  const DEFAULT_SORT_CONFIG = [
    {
      key: 'startDate',
      reverse: true
    }
  ];

  vm.isInitilized = false;
  vm.contents = {};
  vm.onVideoThumbnailClick = onVideoThumbnailClick;
  vm.AppConstants = AppConstants;
  vm.getContentList = getContentList;
  vm.init = init;
  vm.getModuleConfig = getModuleConfig;
  vm.getModuleComponents = getModuleComponents;
  vm.elementClickCallback = elementClickCallback;
  vm.isPxcomAdvertisementEnabled = isPxcomAdvertisementEnabled;
  vm.currentLanguage = LanguageService.getCurrentLanguage();

  const contentTypeAndMetadataMapping = {
    [CONTENT_TYPES.MAGAZINES]: getMagazineMetadata,
    [CONTENT_TYPES.VIDEOS]: getVideoMetadata,
    [CONTENT_TYPES.GAMES]: getGameMetadata,
    [CONTENT_TYPES.VIDEO]: getVideoMetadata,
    [CONTENT_TYPES.PRODUCTS]: getProductMetadata,
    [CONTENT_TYPES.HEADER]: getHeadersMetadata,
    [CONTENT_TYPES.EPUB]: getEpubMetadata,
  };

  function getModuleConfig() {
    return ContentServiceV3.getModuleConfig(vm.modules);
  }

  function getModuleComponents(module) {
    return module.components;
  }

  function filterContentByUrlParameters(contentObject) {
    return SUPPORTED_FILTER_PARAMS.every(param => $stateParams[param] === null ||
      UtilService.hasCommonElements($stateParams[param], ContentServiceV3.extractCategoryOrTags(contentObject, param)));
  }

  function getAllContentsOfTypes(contentTypes) {
    return contentTypes.reduce((acc, contentType) => {
      const contentList = vm.contents[contentType.toLowerCase()] || [];
      return [...acc, ...contentList]
    }, [])
  }

  function getDataByDataModule(moduleConfig) {
    const fetchComponentDataPromises = {};
    const deferred = $q.defer();
    _.each(moduleConfig.components, (component) => {
      _.each(component.dataModules, (dataModule) => {
        const key = dataModule.toLowerCase();
        if (dataModule.toLowerCase() === CONTENT_TYPES.VIDEOS) {
          dataModule = CONTENT_TYPES.VIDEO;
        }
        if (dataModule.toLowerCase() === CONTENT_TYPES.PRODUCTS) {
          dataModule = CONTENT_TYPES.PRODUCT;
        }
        if (dataModule.toLowerCase() === CONTENT_TYPES.IMAGES) {
          dataModule = CONTENT_TYPES.IMAGE;
        }
        fetchComponentDataPromises[key] = ContentApiService.getContentByType(dataModule.toLowerCase());
      });
    });
    $q.all(fetchComponentDataPromises).then((results) => {
      for (let result in results) {
        vm.contents[result] = results[result].data;
      }
      deferred.resolve(vm.contents);
    });
    return deferred.promise;
  }

  function initializeComponentContent(moduleConfig) {
    const airfilyticsEventName = moduleConfig.airfilyticsLabel || moduleConfig.id;
    CrewServiceV2.initSocket();
    moduleConfig.contentId = vm.contentId ? vm.contentId : null;
    AirfilyticsService.catalogPageOpenedEvent(airfilyticsEventName, moduleConfig);
    content = moduleConfig.components
      .filter(componentConfig => componentConfig.dataModules)
      .map(componentConfig => {
        componentConfig.dataModules.filter(dataModule => dataModule === CONTENT_TYPES.PRODUCTS.toUpperCase() ?
          ContentServiceV3.setProductByCategories(getAllContentsOfTypes(componentConfig.dataModules)) :
          {})
        const contentSortConfiguration = componentConfig.sortOrderConfig &&
          componentConfig.sortOrderConfig.length && componentConfig.sortOrderConfig || DEFAULT_SORT_CONFIG;
        let filteredContent = getAllContentsOfTypes(componentConfig.dataModules)
          .filter(filterContentByUrlParameters)
          .filter(ContentServiceV3.filterExpiredContent)
          .filter(contentObject => CustomFilterService.filterByFilteringRules(
            componentConfig.filteringRulesV2, 
            componentConfig.filteringRules, 
            contentObject)
          )
          .filter(contentObject => 
            ContentServiceV3.filterByLanguage(
              componentConfig, 
              contentObject, 
              moduleConfig, 
              vm.settings)
          )
        filteredContent = ContentServiceV3.sortContentList(filteredContent, contentSortConfiguration);
        filteredContent = limitToFilter(filteredContent, componentConfig.limit || filteredContent.length);
        return { key: componentConfig.id, contents: filteredContent };
      })
      .reduce((acc, component) => Object.assign({}, acc, { [component.key]: component.contents }), {});
    if (ApplicationService.isAirFiAdvertisementsEnabled()) {
      UtilService.retry(checkAndDispatchAdsContainerReadyEvent);
    }
  }

  function init() { 
    vm.contentId = $stateParams.id;
    vm.moduleContentMapping = ApplicationService.getContentModuleMappingDetails();
    vm.moduleContentMetadataMapping = createModuleContentMetadataMapping();
    if (vm.moduleContentMetadataMapping[$stateParams.module]) {
      vm.moduleContentMetadataMapping[$stateParams.module]();
    }
    if (CrewServiceV2.isAnnouncementInProgress()) {
      AnnouncementService.openModal();
    }
    ContentApiService.getApplicationModules().then((appConfig) => {
      vm.modules = appConfig.data.modules;
      vm.settings = appConfig.data.settings;
      const moduleConfig = getModuleConfig();
      getDataByDataModule(moduleConfig).then(contents => {
        vm.isInitilized = true;
        initializeComponentContent(moduleConfig);
      });
    });
  }

  function checkAndDispatchAdsContainerReadyEvent() {
    let isEventDispatched = false;
    if ($window.ishtehaarScriptLoaded) {
      const leaderboardContainerReadyEvent = new CustomEvent(AppConstants.BROADCASTED_EVENTS.LEADERBOARD_CONTAINER_READY, {});
      $window.dispatchEvent(leaderboardContainerReadyEvent);
      isEventDispatched = true;
    }
    return isEventDispatched;
  }

  function createModuleContentMetadataMapping() {
    return Object.keys(vm.moduleContentMapping)
      .map(contentType => [vm.moduleContentMapping[contentType].subState, contentTypeAndMetadataMapping[contentType]])
      .reduce((acc, contentTypeAndModule) =>
        Object.assign(acc, { [contentTypeAndModule[0]]: contentTypeAndModule[1] }), {});
  }

  function getContentList(componentConfig) {
    return content[componentConfig.id];
  }

  function gotoElementState(content) {
    let { redirectUrl } = content;
    if (redirectUrl) {
      //todo: remove this hack once all the redirectURL's are updated and the word /dynamic removed from them.
      redirectUrl = redirectUrl.replace('/dynamic', '');
      $location.url(redirectUrl);
    } else {
      //todo: contentType and dataModule should be same for a generic solution
      const stateConfig = getStateConfig(content);
      $state.go(stateConfig.name, stateConfig.options, { reload: false });
    }
  }

  function takeActionBasedOnContentType(content) {
    if (content.type === CONTENT_TYPES.MUSIC) {
      Store.dispatch(Actions.MUSIC_PLAYER.PLAY_TRACK, content);
    } else {
      gotoElementState(content);
    }
  }

  function getStateConfig(content) {
    let contentType = content.type;
    if (contentType === CONTENT_TYPES.VIDEO) {
      contentType = CONTENT_TYPES.VIDEOS;
    } else if (contentType === CONTENT_TYPES.PRODUCT) {
      contentType = CONTENT_TYPES.PRODUCTS;
    }
    const moduleContentMapping = vm.moduleContentMapping[contentType];
    return {
      name: moduleContentMapping.mainState,
      options: {
        module: moduleContentMapping.subState,
        id: content.id,
      }
    };
  }

  function addContent(elementContent, elementType) {
    AirfilyticsService.clickedContentEvent(elementContent, elementType);
    takeActionBasedOnContentType(elementContent);
  }

  function onVideoThumbnailClick(content) {
    if (PremiumContentService.checkContentIsPremium(content)) {
      PremiumContentService.checkUserHasPremiumContentAccess().then((userHasPremiumContentAccess) => {
        if (userHasPremiumContentAccess) {
          takeActionBasedOnContentType(content);
        } else {
          const stateConfig = getStateConfig(content);
          LocationService.cacheRedirectState(stateConfig);
          $rootScope.$broadcast('PREMIUM_CONTENT_ACCESS_REQUESTED');
        }
      })
    } else {
      vm.userHasPremiumContentAccess = true;
    }
  }

  function elementClickCallback(elementContent, elementType) {
    if (elementContent && angular.isArray(elementContent)) {
      Object.keys(elementContent).forEach(key => {
        addContent(elementContent[key], elementType)
      });
    } else {
      addContent(elementContent, elementType)
    }
  }

  function isPxcomAdvertisementEnabled() {
    return ApplicationService.getPxcomConfig().enabled;
  }

  function setContentMetadataForMagazine(metaFileUrl, magazineDetails, isOldVersionMagazine) {
    ApiService.call(HTTP_METHODS.GET, metaFileUrl)
      .then((response) => {
        const magazineMetadata = response.data;
        vm.contentMetadata =
          {
            magazineDetails,
            totalPages: isOldVersionMagazine ? magazineMetadata.numpages : magazineMetadata.totalPages,
            isOldVersionMagazine: isOldVersionMagazine,
            magazinePath: isOldVersionMagazine ? `${magazineDetails.media.root}/assets` : '',
            images: !isOldVersionMagazine ? magazineMetadata.files.map(file => `${magazineDetails.media.root}/${file}`) : []
          };
      });
  }

  function getMagazineMetadata() {
    ContentApiService.getContentById(vm.contentId).then(content => {
      const magazineDetails = content.data;
      if (magazineDetails.media.file.indexOf('/assets') > -1) {
        const metaFileUrl = `${magazineDetails.media.root}/assets/info.json`;
        setContentMetadataForMagazine(metaFileUrl, magazineDetails, true);
      } else {
        const metaFileUrl = `${magazineDetails.media.root}/${magazineDetails.id}.json`;
        setContentMetadataForMagazine(metaFileUrl, magazineDetails, false);
      }
    })
  }

  function getVideoMetadata() {
    ContentApiService.getContentById(vm.contentId).then(content => {
      vm.contentMetadata = content.data;
    })
  }

  function getGameMetadata() {
    ContentApiService.getContentById(vm.contentId).then(content => {
      vm.contentMetadata = content.data;
    })
  }

  function getProductMetadata() {
    ContentApiService.getContentById(vm.contentId).then(content => {
      let productDetails = content.data
      vm.contentMetadata = { productDetails };
    });
  }

  function getHeadersMetadata() {
    ContentApiService.getContentById(vm.contentId).then(content => {
      let headerDetails = content.data
      vm.contentMetadata = { headerDetails };
    })
  }

  function getEpubMetadata() {
    if(vm.contentId){
      ContentApiService.getContentById(vm.contentId).then(content => {
        vm.contentMetadata = content.data;
      })
    }

  }

}

angular.module('airfi.platform.news').factory('NewsService', function newsService($q, airfiPlatformConstants,
  ApiService, AirfilyticsService) {
  const LIMIT = 20;

  let offset = 0;
  let totalRows = null;

  const articles = {
    list: [],
  };
  const publishers = {
    list: [],
  };
  let selectedPublisher = null;
  let isInitialized = false;

  function fetchPublishers() {
    const def = $q.defer();

    ApiService.call('get', ApiService.routes.articlesMetadata())
      .then((data) => {
        def.resolve(data);
      },
        (err) => {
          def.reject(err);
        });
    return def.promise;
  }

  function fetchArticles(options) {
    const def = $q.defer();
    ApiService.call('get', ApiService.routes.articles(), {
      params: options,
    })
      .then((data) => {
        def.resolve(data);
      }, (err) => {
        def.reject(err);
      });
    return def.promise;
  }

  function getArticleImageUrl(article) {
    let imageURL = '';
    if (article._attachments) {
      imageURL = `${airfiPlatformConstants.API_URL}/attachment/${encodeURIComponent(article._id)}/${article._id.split('/')[
        1]}.jpeg`;
    }
    return imageURL;
  }

  function injectImageUrl(articleMetaData) {
    const article = articleMetaData.doc;
    article.imageURL = getArticleImageUrl(articleMetaData.doc);
    return article;
  }

  function loadMoreArticles() {
    const deferred = $q.defer();
    if (totalRows === null || offset < totalRows) {
      const params = {
        max: LIMIT,
        offset,
      };
      if (selectedPublisher) {
        params.publisher = selectedPublisher;
      }

      fetchArticles(params)
        .then((result) => {
          offset += result.data.rows.length;
          totalRows = result.data.total_rows;
          const mappedArticles = _.map(result.data.rows, injectImageUrl);
          articles.list = _.concat(articles.list, mappedArticles);
          deferred.resolve(articles);
        }, deferred.reject);
    } else {
      deferred.resolve(articles);
    }
    return deferred.promise;
  }


  function init() {
    if (!isInitialized) {
      const promises = {
        publishers: fetchPublishers(),
      };
      $q.all(promises).then((result) => {
        publishers.list = Object.keys(result.publishers.data.publishers);
        isInitialized = true;
      });
    }
  }

  function getArticle(id) {
    const def = $q.defer();
    const article = _.find(articles.list, currentArticle => currentArticle._id === `article/${id}`);

    if (article) {
      def.resolve(article);
    } else {
      ApiService.call('get', ApiService.routes.article(), { params: { id } })
        .then((result) => {
          result.data.imageURL = getArticleImageUrl(result.data); // eslint-disable-line
          def.resolve(result.data);
        }, (err) => {
          def.reject(err);
        });
    }
    return def.promise;
  }

  function getPublishers() {
    return publishers;
  }

  function getArticles() {
    return articles;
  }

  function getSelectedPublisher() {
    return selectedPublisher;
  }

  function updatePublisher(publisher) {
    offset = 0;
    totalRows = null;
    selectedPublisher = publisher;
    articles.list = [];
  }

  function sendOpenedNewsArticleEvent(content) {
    const { description, title, published, type, link, summary } = content;
    const contentMetadata = { publisher: description, title, published, type, link, summary };
    const eventname = 'Opened News Article'
    return AirfilyticsService.send(eventname, contentMetadata);
  }

  return {
    init,
    loadMoreArticles,
    getPublishers,
    getArticles,
    getSelectedPublisher,
    updatePublisher,
    getArticle,
    sendOpenedNewsArticleEvent,
  };
});

angular
  .module('airfi.platform.shop')
  .controller('OrderFulfilmentController', orderFulfilmentController);

function orderFulfilmentController($stateParams, $state, UserService, ShopService, ShopAirfilyticsService,
  LocationService, PremiumContentAirfilyticsService) {
  const vm = this;
  vm.order = null;
  vm.orderInfo = {};

  vm.initialize = initialize;
  vm.getProductImageUrl = ShopService.getProductImageUrl;
  vm.billingDescriptor = null;
  vm.getTotalAmount = getTotalAmount;

  const ORDER_SUCCESS_STATUS = 'SUCCESS';
  const PRODUCT_TYPE_VOUCHER = 'Voucher';

  function initialize() {
    const paymentId = $stateParams.paymentId;
    ShopService.initialize().then((data) => {
      const paymentMethod = data.cart.paymentMethod;
      if (!data.cart.lineItems.length) {
        $state.go('shop.catalog');
      } else if (paymentMethod) {
        ShopService.convertCartToOrder(paymentMethod, paymentId, data.cart.uid).then((response) => {
          ShopService.getBillingDescriptor().then((billingDescriptor) => {
            vm.billingDescriptor = billingDescriptor;
          });
          const result = response.data;
          if (result.status === ORDER_SUCCESS_STATUS) {
            ShopAirfilyticsService.orderCompleteSuccess(result.order);
            ShopService.clearCart();
            vm.order = result.order;
            if (vm.order.order_identifier) {
              const orderId = vm.order.order_identifier.split('-');
              vm.orderInfo.orderId = orderId[orderId.length - 1];
            }
            if (vm.order.timestamp) {
              vm.orderInfo.orderTimestamp = new Date(vm.order.timestamp).toString();
            }
            vm.message = result.message;
            vm.order.line_items.forEach((item) => {
              if (item.product.product_type === PRODUCT_TYPE_VOUCHER && angular.isDefined(item.product.accessCode)) {
                $state.go(
                    LocationService.getCachedRedirectState().name,
                    LocationService.getCachedRedirectState().options
                  )
                  .then(() => PremiumContentAirfilyticsService.premiumContentAccessActivated(
                    'Purchase',
                    item.product.accessCode
                  ));
              }
            });
          } else {
            vm.errorMessage = result.message;
            ShopAirfilyticsService.orderCompleteError(result.message)
          }
        });
      } else {
        $state.go('shop.deliverypaymentdetails');
      }
    });
  }

  function getTotalAmount() {
    return vm.order ?
      vm.order.line_items.reduce((total, lineItem) => total + (lineItem.effectivePrice * lineItem.quantity), 0) : '';
  }
}

