angular
  .module('airfi.platform.service')
  .factory('ApiService', function ApiService($http, airfiPlatformConstants) {
    const urlBase = airfiPlatformConstants.API_URL || '';
    const apiRoutes = {
      root: () => '/',
      getInfo: () => '/getInfo',
      getId: () => '/getId',
      musicAll: () => '/music/all',
      contentDistroStatus: () => '/api/v2/crew/system/content/status',
      battery: () => '/battery',
      stockLevels: () => '/stockLevels',
      unidentify: () => '/unidentify',
      adsConfiguration: () => '/ads-configuration',
      crewlogin: () => '/crew/login',
      refreshCrewLogin: () => '/crew/session/refresh',
      articles: () => '/articles',
      articlesMetadata: () => '/articles/metaData',
      articlesCount: () => '/articlesCount',
      article: () => '/article',
      statsAll: () => '/stats/all',
      statsConnectedClients: () => '/stats/connectedclients',
      statsConcurrentStreamers: () => '/stats/concurrentstreamers',
      voucherAvailable: () => '/voucher/available',
      voucherValidateUser: () => '/voucher/validateUser',
      voucherValidate: (voucherId, userName, seatNumber) =>
        `/voucher/${voucherId}/validate?userName=${userName}&seatNumber=${seatNumber}`,
      voucherDetails: voucherId => `/voucher/${voucherId}/details`,
      voucherGenerate: () => '/voucher/generate',
      getAllVouchers: () => '/voucher/all',
      disableVoucher: voucherId => `/voucher/${voucherId}`,
      getMusicThumbnail: uri => `/${uri}`,
      processOrder: (paymentId, purhcaseId) =>
        `/order/process?paymentId=${paymentId}&purchaseId=${purhcaseId}`,
      flightShutdown: () => '/flight/shutdownAirFi',
      endFlightAndShutdownAirfi: () => '/flight/endFlight',
      convertCartToOrder: () => '/cart/convert',
      availableCurrencies: () => '/payment/availableCurrencies',
      applicationModules: () => '/application/getAllModules',
      airlineDetails: () => '/application/airlineDetails',
      saveForm: () => '/form/save',
      getUserForms: () => '/form/getUserForms',
      getAllForms: () => '/form/getAllForms',
      getPublicKey: () => '/application/getPublicKey',
      allContent: () => '/api/v3/content/catalog',
      getPromotions: () => '/api/v2/shop/promotions',
      contentByType: contentType => `/api/v3/content/catalog/${contentType}`,
      contentById: contentId => `/api/v3/content/${contentId}`,
      checkUserSubmittedForm: formName => `/user/form?submitted=${formName}`,
      markUserSubmittedForm: formName => `/user/form?submitted=${formName}`,
      checkGSMStatus: () => 'crew/system/statusgsm',
      onConnectivityMobileNetwork: () => '/api/v2/crew/system/connectivity/mobile/on',
      offConnectivityMobileNetwork: () => '/api/v2/crew/system/connectivity/mobile/off',
      triggerContentSync: () => '/api/v2/crew/system/content/sync',
      airports: () => '/api/v2/crew/flight/airports',
      aircrafts: () => '/api/v2/crew/flight/aircrafts',
      getUserMessagingHistory: () => '/api/v1/messaging/getUserMessagingHistory',
      getUserDetails: () => '/v1/user/details',
      updateUserDetails: () => '/v1/user/details',
      getShopSettings: () => '/shop/settings',
      updateShopSettings: () => '/shop/settings'
    };
    const getFullUrl = url => urlBase + url;
    const api = {};

    // required for cross domain cookies to work
    if (airfiPlatformConstants.name === 'development') {
      $http.defaults.withCredentials = true; // eslint-disable-line
    }

    // Automate the routes to be prefixed with 'getFullUrl()'
    Object.keys(apiRoutes).forEach(routeId => {
      const routeF = apiRoutes[routeId];
      apiRoutes[routeId] = (...args) => getFullUrl(routeF.apply(this, args));
    });

    // Export api methods
    api.routes = apiRoutes;
    api.call = (method, url, params = {}, config = {}) => {
      let httpMethod;
      switch (method) {
        case 'post':
          httpMethod = $http.post;
          break;
        case 'put':
          httpMethod = $http.put;
          break;
        case 'delete':
          httpMethod = $http.delete;
          break;
        default:
          httpMethod = $http.get;
          break;
      }
      return httpMethod(url, params, config);
    };
    return api;
  });

angular.module('airfi.platform.service')
  .factory('FormService', function formService($q, $stateParams, $window, FlightService,
    UserService, AirfilyticsService, DataService, ApiService, ApplicationService,
    AppConstants, LanguageService, UtilService) {

    function submitForm(moduleName, provider, eventName, formData) {
      return new Promise((resolve, reject) => {
        moduleName = moduleName || $stateParams.module;
         /* eslint-disable */
        FlightService.loadFlightConfiguration().then(function (flightConfig) {
          const {
            numOfPax,
            aircraftRegisterType,
            tailNumber,
            originAirportName,
            arrivalAirportName,
            boxId
          } = flightConfig || {};
          const {
            origin,
            destination,
            date,
            flight_number
          } = flightConfig && flightConfig.flight_leg_identifier || {};
          const userId = UserService.getUUID();
          formData.flightNumber = flight_number;
          formData.originAirportName = originAirportName;
          formData.arrivalAirportName = arrivalAirportName;
          formData.boxId = boxId;
          formData.currentTime = new Date().toUTCString();
          const dataToSubmit = {
            formData: formData,
            module: moduleName,
            provider: provider,
            formName: formData.formName,
            displayName: formData.formName,
            userId: userId,
            userName: UserService.getUser() ? UserService.getUser().name : '',
            userSeat: UserService.getUser() ? UserService.getUser().seat : '',
            flightDate: date,
            flightNumber: flight_number,
            origin: origin,
            destination: destination,
            numberOfPassenger: numOfPax,
            aircraftRegistration: aircraftRegisterType,
            aircraftType: tailNumber,
            client: {
              browser: UtilService.getBrowserAndOsDetails().browser ? UtilService.getBrowserAndOsDetails().browser : $window.navigator.userAgent,
              operatingSystem: $window.navigator.platform,
              language: LanguageService.getCurrentLanguage(),
              resolution: `${$window.screen.width}x${$window.screen.height}`,
            }
          };

          //insert marketing_carrier_codes if it is present 
          if (flightConfig && flightConfig.flight_leg_identifier.marketing_carrier_codes) {
            dataToSubmit.marketing_carrier_codes = flightConfig.flight_leg_identifier.marketing_carrier_codes ;
          }
          /* eslint-disable */
          const stringForm = JSON.stringify(dataToSubmit);
          const _id = `form/${provider}/${moduleName}/${userId}/${formData.formName.replace(/\s/g, '-')}/${Date.now()}`

          encryptForm(stringForm, formData, _id)
            .then(encryptedForm => {
              ApiService.call(AppConstants.HTTP_METHODS.POST, ApiService.routes.saveForm(), encryptedForm, {}).then(() => {
                const airfiLyticsData = { ...dataToSubmit }
                delete airfiLyticsData.formData
                ApiService.call(
                  AppConstants.HTTP_METHODS.POST,
                  ApiService.routes.markUserSubmittedForm(moduleName.toLowerCase()));
                AirfilyticsService.send(eventName, airfiLyticsData);
                updateUserDetailsAndRegistrationFormData(formData);
                resolve();
              });
            }).then(() => {

            }, () => {
              reject('Failed to submit the form');
            })
        })
      })
    }

    function encryptForm(stringForm, formData, _id) {
      return new Promise((resolve, reject) => {
        const encryptionKey = sjcl.random.randomWords(5, 10).toString();
        const rsa = new JSEncryptExports.JSEncrypt();
        ApplicationService.getPublicKey()
          .then(result => {
            const rsaPublicKey = result.data
            rsa.setPublicKey(rsaPublicKey)
            const encryptedForm = {
              formData: sjcl.encrypt(encryptionKey, stringForm),
              key: rsa.encrypt(encryptionKey),
              type: 'form',
              processed: false,
              version: 2,
              formName: formData.formName,
              userId: UserService.getUUID(),
              timestamp: new Date().toISOString(),
              _id
            }
            resolve(encryptedForm)
          }, (err) => {
            reject(err)
          })
      })
    }

    function getUserForms(formName) {
      const params = { userId: UserService.getUUID() };
      if (formName) {
        params.formName = formName;
      }
      return new Promise((resolve, reject) => {
        ApiService.call('get', ApiService.routes.getUserForms(), { params: params }, {})
          .then((result) => {
            resolve(result.data);
          }, (error) => {
            reject(error);
          });
      });
    }

    function getAllForms() {
      return ApiService.call(AppConstants.HTTP_METHODS.GET, ApiService.routes.getAllForms())
    }

    function updateUserDetailsAndRegistrationFormData(formData) {
      const registrationFormMappingConfig = ApplicationService.getRegistrationFormMappingConfig();
      if (registrationFormMappingConfig && registrationFormMappingConfig.formName === formData.formName) {
        const mappedData = getMappedDataFromRegistrationFrom(registrationFormMappingConfig, formData);
        UserService.updateUserDetailsIntoLocalStroage(mappedData);
      }
    }

    function getMappedDataFromRegistrationFrom(config, registrationFormData) {
      const mappedData = {}
      for(let key in config.formData) {
        mappedData[key] = registrationFormData[config.formData[key]] || null;
      }
      return mappedData;
    }

    return {
      submitForm,
      getUserForms,
      getAllForms,
      getMappedDataFromRegistrationFrom
    };
  });

angular.module('airfi.platform.service').factory('MusicPlayerActions', function Action( RxJS, Store ) {

  return {
      PLAY_TRACK: playTrack,
      NEXT_TRACK: nextTrack,
      PREVIOUS_TRACK: previousTrack,
      ADD_AT_END_OF_PLAYLIST: pushToPlaylist,
      CHANGE_TRACK: changeTrack,
      MOVE_UP_IN_PLAYLIST: moveUp,
      MOVE_DOWN_IN_PLAYLIST: moveDown,
      DELETE_TRACK: deleteTrack,
      CLOSE_PLAYER: closePlayer
  };

  function updateMusicPlayer(state, musicPlayer) {
    const userData = { ...state.userData };
    userData.musicPlayer = musicPlayer;
    Store.setState(Object.assign({}, state, { userData: { ...userData } }));
  }

  function closePlayer(state) {
    const musicPlayer = { ...state.userData.musicPlayer };
    musicPlayer.isMusicPlayerActive = false;
    musicPlayer.playlist = [];
    updateMusicPlayer(state, musicPlayer);
  }

  function isTrackAlreadyAdded(state, track) {
    return state.userData.musicPlayer.playlist.some((playlistTrack) => {
      return track.id === playlistTrack.id
    });
  }

  function playTrack(state, track) {
    const playlist = state.userData.musicPlayer.playlist;
    if (!isTrackAlreadyAdded(state, track)) {
      playlist.push(track);
    }
    if (!state.userData.musicPlayer.isMusicPlayerActive) {
      activateMusicPlayer(state, track);
    } else {
      var filteredPlaylist = playlist.filter(function(playlistTrack){
        return track.id !== playlistTrack.id
      });
      const musicPlayer = {
        playlist: [track, ...filteredPlaylist],
        currentTrackIndex: 0,
        isMusicPlayerActive: true
      };
      updateMusicPlayer(state, musicPlayer);
    }
  }

  function activateMusicPlayer(state,track){
    var updatedTracks = state.userData.musicPlayer.playlist.filter(function (existingTrack) {
      return existingTrack.contentId != track.contentId;
    });
    const musicPlayer = {
      playlist: [track, ...updatedTracks],
      currentTrackIndex: 0,
      isMusicPlayerActive: true
    };
    updateMusicPlayer(state, musicPlayer);
  }

  function nextTrack(state) {
    const musicPlayer = { ...state.userData.musicPlayer };
    const noOfSongsInPlaylist = musicPlayer.playlist.length;
    musicPlayer.currentTrackIndex = musicPlayer.currentTrackIndex < noOfSongsInPlaylist - 1 ?
      musicPlayer.currentTrackIndex + 1 : 0;
    updateMusicPlayer(state, musicPlayer);
  }

  function previousTrack(state) {
    const musicPlayer = { ...state.userData.musicPlayer };
    const noOfSongsInPlaylist = musicPlayer.playlist.length;
    musicPlayer.currentTrackIndex = musicPlayer.currentTrackIndex === 0 ?
      noOfSongsInPlaylist - 1 : musicPlayer.currentTrackIndex - 1;
    updateMusicPlayer(state, musicPlayer);
  }

  function pushToPlaylist(state, payload) {
    const musicPlayer = { ...state.userData.musicPlayer };
    musicPlayer.playlist = Array.isArray(payload) ? [...payload] : [...musicPlayer.playlist, payload];
    if(!musicPlayer.isMusicPlayerActive) {
      musicPlayer.currentTrackIndex = 0;
      musicPlayer.isMusicPlayerActive= true;
    }
    updateMusicPlayer(state, musicPlayer);
  }

  function changeTrack(state, trackIndex) {
    const musicPlayer = { ...state.userData.musicPlayer };
    musicPlayer.currentTrackIndex = trackIndex;
    updateMusicPlayer(state, musicPlayer);
  }

  function moveUp(state, index) {
    const musicPlayer = { ...state.userData.musicPlayer };
    const songToBeMoved = musicPlayer.playlist[index];
    musicPlayer.playlist.splice(index, 1);
    musicPlayer.playlist.splice(index - 1, 0, songToBeMoved);
    updateMusicPlayer(state, musicPlayer);
  }

  function moveDown(state, index) {
    const musicPlayer = { ...state.userData.musicPlayer };
    const songToBeMoved = musicPlayer.playlist[index];
    musicPlayer.playlist.splice(index, 1);
    musicPlayer.playlist.splice(index + 1, 0, songToBeMoved);
    updateMusicPlayer(state, musicPlayer);
  }

  function deleteTrack(state, index) {
    const musicPlayer = { ...state.userData.musicPlayer };
    musicPlayer.playlist.splice(index, 1);
    if (index < musicPlayer.currentTrackIndex) {
      musicPlayer.currentTrackIndex = musicPlayer.currentTrackIndex - 1;
    }
    updateMusicPlayer(state, musicPlayer);
  }

});

