angular
  .module('airfi.platform.service')
  .factory('PremiumContentService', premiumContentService);
function premiumContentService($uibModal, $rootScope, $q, $state,
  ApiService, UserService, FlightService, ApplicationService, DataService,
  ShopServiceV2, LocationService) {
  const HAS_PREMIUM_CONTENT_ACCESS = true;
  const VIRTUAL_PRODUCT_ID = 'Product:Virtual';
  const LIMIT = 10;
  let vouchersList = [];
  let offset = 0;
  let totalRows = null;

  let premiumContentIntroModal = null;
  let premiumContentCheckPostState = null;

  const premiumContentAccessListener = $rootScope.$on('PREMIUM_CONTENT_ACCESS_REQUESTED', () => {
    showPremiumContentIntro();
  });
  $rootScope.$on('$destroy', premiumContentAccessListener);

  checkUserHasPremiumContentAccess();


  return {
    checkUserHasPremiumContentAccess,
    validateVoucherForPremiumContent,
    getvoucherDetails,
    createNewVoucher,
    validateUser,
    loadVouchers,
    getAllVouchers,
    disableVoucher,
    checkContentIsPremium,
    showPremiumContentIntro,
    getPremiumContentCheckPostRedirectionState,
    addVoucherToCart,
  };

  function addVoucherToCart() {
    return new Promise((resolve, reject) => {
      ShopServiceV2.initialize().then(() => {
        DataService.fetchDocs(VIRTUAL_PRODUCT_ID)
          .then((result) => {
            const virtualProduct = result.rows[0];
            ShopServiceV2.addProductToCart(virtualProduct.doc).then(() => {
              resolve();
            }, reject);
          }, reject);
      }, reject);
    });
  }


  function getPremiumContentCheckPostRedirectionState() {
    return premiumContentCheckPostState;
  }

  function checkContentIsPremium(content) {
    let contentIsPremium = false;
    if (content && content.tags) {
      content.tags.forEach((tag) => {
        if (tag.toLowerCase() === 'premium' && !contentIsPremium) {
          contentIsPremium = true;
        }
      });
    }
    return contentIsPremium;
  }

  function checkUserHasPremiumContentAccess() {
    return new Promise(((resolve, reject) => {
      const premiumContentConfig = ApplicationService.getPremiumContentConfig();
        if (premiumContentConfig.payForPremiumContent) {
            if (FlightService.getPremiumContentAccessStatus()) {
              resolve(HAS_PREMIUM_CONTENT_ACCESS);
            } else if (!UserService.isUserIdentified) {
              resolve(!HAS_PREMIUM_CONTENT_ACCESS);
            } else {
              validateUser().then((result) => {
                const resultOfUserContentAccessCheck = Boolean(result.valid);
                resolve(resultOfUserContentAccessCheck);
              }, reject);
            }
        } else {
          resolve(HAS_PREMIUM_CONTENT_ACCESS);
        }
    }));
  }

  function validateUser() {
    return new Promise(((resolve, reject) => {
      ApiService.call('get', ApiService.routes.voucherValidateUser()).then((result) => {
        resolve(result.data);
      }, reject);
    }));
  }

  function validateVoucherForPremiumContent(voucherCode, userName, seatNumber) {
    return new Promise(((resolve, reject) => {
      ApiService.call('get', ApiService.routes.voucherValidate(voucherCode, userName, seatNumber)).then((result) => {
        resolve(result.data);
      }, reject);
    }));
  }

  function getvoucherDetails(voucherCode) {
    return ApiService.call('get', ApiService.routes.voucherDetails(voucherCode.toUpperCase()));
  }

  function createNewVoucher(payload) {
    return new Promise(((resolve, reject) => {
      const crewCredentials = UserService.getCrewCredentials();
      ApiService.call('post', ApiService.routes.voucherGenerate(), payload, {
        headers: {
          crewSession: crewCredentials.sessionId,
        },
      }).then((response) => {
        offset = 0;
        vouchersList = [];
        totalRows = null;
        resolve(response.data);
      }, reject);
    }));
  }

  function getAllVouchers() {
    return vouchersList;
  }

  function loadVouchers() {
    const deferred = $q.defer();
    if ((totalRows === null || offset < totalRows) && vouchersList.length !== totalRows) {
      const params = {
        max: LIMIT,
        skipBy: offset,
      };

      ApiService.call('get', ApiService.routes.getAllVouchers(), {
        params
      }).then((result) => {
        offset += result.data.vouchers.length;
        totalRows = result.data.queryStatistics.totalCount;
        vouchersList = _.concat(vouchersList, result.data.vouchers);
        deferred.resolve(vouchersList);
      }, deferred.reject);
    } else {
      deferred.reject
    }
    return deferred.promise;
  }

  function disableVoucher(voucherCode) {
    return ApiService.call('delete', ApiService.routes.disableVoucher(voucherCode));
  }

  function showPremiumContentIntro() {
    if (!premiumContentIntroModal) {
      premiumContentIntroModal = $uibModal.open({
        backdrop: 'true',
        templateUrl: 'app/components/generic/premium-content/premium-content-intro.html',
        controller: 'PremiumContentIntroController',
        controllerAs: 'vm' });
      // when the user clciks buy and the next modal is shown
      premiumContentIntroModal.result.then(() => {
        premiumContentIntroModal = null;
      });
      // if the user closed the modal without procceding ahead
      premiumContentIntroModal.closed.then(() => {
        premiumContentIntroModal = null;
      });
    }
  }
}

