angular.module('airfi.platform.service')
    .factory('StockService', function stockService(DataService, UserService, $q, ApiService) {
      let outOfStockProducts = {}; // hash of "Out of Stock" products id's

      function putProductOutOfStock(productId) {
        const deferred = $q.defer();
        if (UserService.isCrew) {
          const data = {
            // this document is added to the stock-level database in order to mark
            // product "Out of Stock"
            doc: {
              _id: `Stock/${productId}`,
              product_id: productId,
              current_stock: 0,
              type: 'Stock',
            },
            uid: UserService.getUUID(),
            crewName: UserService.name, // required for crew member authentication on server
            crewSession: UserService.crew.sessionId, // required for crew member authentication
                                                  // on server
          };
          DataService.addDoc(data).then(() => {
            outOfStockProducts[productId] = true;
            deferred.resolve();
          }, deferred.reject);
        } else {
          deferred.reject();
        }
        return deferred.promise;
      }

      function putProductInStock(productId) {
        const deferred = $q.defer();
        if (UserService.isCrew) {
          const data = {
            doc: { // this document is removed from the stock-level database
                   // in order to mark product "in Stock"
              _id: `Stock/${productId}`,
              product_id: productId,
              current_stock: 0,
              type: 'Stock',
            },
            uid: UserService.getUUID(),
            crewName: UserService.name, // required for crew member authentication on server
            crewSession: UserService.crew.sessionId, // required for crew member authentication
                                                  // on server
          };

          DataService.removeDoc(data).then(() => {
            delete outOfStockProducts[productId];
            deferred.resolve();
          }, deferred.reject);
        } else {
          deferred.reject();
        }
        return deferred.promise;
      }

        // Puts all the "Out of Stock" products in Stock
      function resetStockLevels() {
        const deferred = $q.defer();
        if (UserService.isCrew) {
          const data = {
            doc: {
                        // when this document is processed on the server,
                        // all the docs in stock-level database with type Stock
                        // are removed from the stock-level database
              _id: `ResetStock/${new Date().toISOString()}`,
              type: 'ResetStock',
            },
            uid: UserService.getUUID(),
            crewName: UserService.name,
            crewSession: UserService.crew.sessionId,
          };

          DataService.processDoc(data, (err, docs) => {
            if (!err) {
              outOfStockProducts = {};
              deferred.resolve(docs);
            } else {
              deferred.reject(err);
            }
          });
        }
        return deferred.promise;
      }

      function getOutOfStockProducts() {
        const deferred = $q.defer();
        if (outOfStockProducts.length > 0) {
          deferred.resolve(outOfStockProducts);
        } else {
          ApiService.call('get', ApiService.routes.stockLevels())
                    .then((stock) => {
                      stock.data.rows.forEach((row) => {
                        outOfStockProducts[row.doc.product_id] = true;
                      });
                      deferred.resolve(outOfStockProducts);
                    }, deferred.reject);
        }
        return deferred.promise;
      }

      function onStockUpdate(callback) {
        DataService.onDownstreamUpdate('Stock', (stock) => {
          if (stock._deleted) {
            delete outOfStockProducts[stock.product_id];
          } else {
            outOfStockProducts[stock.product_id] = true;
          }
          callback(stock.product_id, !stock._deleted);
        }, true);
      }

      function onStockReset(callback) {
        DataService.onDownstreamUpdate('ResetStock', (result) => {
          outOfStockProducts = {};
          callback(result);
        }, true);
      }

      return {
        putProductOutOfStock,
        putProductInStock,
        resetStockLevels,
        getOutOfStockProducts,
        onStockUpdate,
        onStockReset,
      };
    });

angular
  .module('airfi.platform.service')
  .factory('UtilService', function utilsService($http, $q, $window, AppConstants, $document) {
    // Private array of chars to use
    const CHARS = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'.split('');
    const isBothArgumentsArray = (firstArgument, secondArgument) => angular.isArray(firstArgument) &&
      angular.isArray(secondArgument);
    const isOnlyFirstArgumentArray = (firstArgument, secondArgument) => angular.isArray(firstArgument) &&
      !angular.isArray(secondArgument);
    const isOnlySecondArgumentArray = (firstArgument, secondArgument) => !angular.isArray(firstArgument) &&
      angular.isArray(secondArgument);
    const getRandomIndex = (list) => Math.floor(Math.random() * list.length);

    return {
      retry,
      uuidFast,
      getUUID,
      getTime,
      isEmptyObject,
      scrollTo,
      allFalse,
      removeSpaces,
      isResourceExist,
      hasCommonElements,
      getRandomIndex,
      oneYearFromNow,
      getCookie,
      getCurrentYear,
      getBrowserAndOsDetails,
      nullObjectFilter,
      getRandomElement: (list) => list[getRandomIndex(list)],
      debounce
    };

    function oneYearFromNow() {
      const now = new Date();
      return new Date(now.getFullYear()+1, now.getMonth(), now.getDate());
    }

    function getCurrentYear() {
      return new Date().getFullYear();
    }

    function isResourceExist(url) {
      const deferred = $q.defer();
      const httpCallArguments = {
        method: 'GET',
        url,
      };
      $http(httpCallArguments).then(() => {
        deferred.resolve(true);
      }, () => {
        deferred.reject(false);
      });
      return deferred.promise;
    }

    function hasCommonElements(firstObject, secondObject) {
      let result = false;
      if (isBothArgumentsArray(firstObject, secondObject)) {
        result = firstObject.every(currentElement => secondObject.includes(currentElement));
      } else if (isOnlyFirstArgumentArray(firstObject, secondObject)) {
        result = firstObject.includes(secondObject);
      } else if (isOnlySecondArgumentArray(firstObject, secondObject)) {
        result = secondObject.includes(firstObject);
      } else {
        result = firstObject === secondObject;
      }
      return result;
    }

    function uuidFast() {
      const chars = CHARS;
      const uuid = [36];
      let rnd = 0;
      let r;
      for (let i = 0; i < 36; i += 1) {
        if (i === 8 || i === 13 || i === 18 || i === 23) {
          uuid[i] = '-';
        } else if (i === 14) {
          uuid[i] = '4';
        } else {
          if (rnd <= 0x02) {
            rnd = 0x2000000 + (Math.random() * 0x1000000);
          }
          r = (rnd & 0xf);
          rnd >>= 4;
          uuid[i] = chars[(i === 19) ? (r & 0x3) | 0x8 : r];
        }
      }
      return uuid.join('');
    }

    function getUUID() {
      return uuidFast();
    }

    function getTime(timestamp) {
      // Given an ISO 8601 timestamp string, return the time in hh:mm format
      const date = new Date(timestamp);
      if (date.toString() === 'Invalid Date') {
        return 'Unknown time';
      }
      const minutes = date.getMinutes();
      return `${date.getHours()}:${(minutes < 10) ? `0${minutes}` : minutes}`;
    }


    function isEmptyObject(object) {
      return jQuery.isEmptyObject(object);
    }

    function scrollTo(elementId) {
      angular.element('html, body').animate({
        scrollTop: angular.element(`#${elementId}`).offset().top - angular.element('header').height(),
      }, 100);
    }

    function allFalse(object) {
      const keys = Object.keys(object);
      for (let i = 0; i < keys.length; i += 1) {
        if (object[keys[i]] === true) {
          return false;
        }
      }
      return true;
    }

    function removeSpaces(string) {
      return string.replace(/ /g, '');
    }

    function getCookie(name) {
      let cookie = {};
      $document[0].cookie.split(';').forEach(function(element) {
        let [key,value] = element.split('=');
        cookie[key.trim()] = value;
      })
      return cookie[name];
    }

    function getBrowserAndOsDetails() {
      const userAgent = $window.navigator.userAgent;
      const vendor = $window.navigator.vendor;
      const isChrome = userAgent.match(/Chrome/) && vendor.match(/Google Inc/);
      const isSafari = userAgent.match(/Safari/) && !userAgent.match(/Chrome/);
      const isAndroid = userAgent.match(/Android/);
      const isIos = userAgent.match(/iPad|iPhone|iPod/);
      let isDrmSupported = true;
      let browser, os;

      if (isChrome) {
        browser = AppConstants.BROWSERS.CHROME;
      } else if (isSafari) {
        browser = AppConstants.BROWSERS.SAFARI;
      }

      if (isAndroid) {
        os = AppConstants.OS.ANDROID;
        isDrmSupported = browser === AppConstants.BROWSERS.CHROME;
      } else if (isIos) {
        os = AppConstants.OS.IOS;
        isDrmSupported = browser === AppConstants.BROWSERS.SAFARI;
      }

      return {
        browser,
        os,
        userAgent,
        vendor,
        isDrmSupported,
      }
    }

    function debounce(func, delay) {
      let inDebounce;
      return function () {
        const context = this;
        const args = arguments;
        clearTimeout(inDebounce);
        inDebounce = setTimeout(() => func.apply(context, args), delay);
      }
    }

    function nullObjectFilter(array,key) {
      return array && array.length ? array.filter(obj => Object.keys(obj[key]).length) : null;
    }

    function retry(tagetFn, maxAttempts=5, interval=2000, attemptNumber=0) {
      if (maxAttempts > attemptNumber) {
          setTimeout(() => {
            let result = tagetFn();
            if (!result) {
              retry(tagetFn, maxAttempts, interval, ++attemptNumber);
            }
          }, interval * attemptNumber);
      }
    }
  });

