angular.module('airfi.platform.service')
  .factory('VideoService', function VideoService($window, ApplicationService, AirfilyticsService,
                                                 VideoTypeConfiguration, AppConstants) {

    let isRatingsPopupShown = false;
    let isAnnouncementInProgress = false;
    const localStorage = $window.localStorage;
    let videoPlaying = false;

    function getPlayerTypeByVideoType(fileExtension) {
      return VideoTypeConfiguration[fileExtension];
    }

    /*
      DEPRECATED
      Keeping this for backward compatibility, logPlayerEvent will be used for logging all required player events
     */
    function logPlayedVideoEvent(currentVideo) {
      const contentMetadata = AirfilyticsService.getContentMetadata(currentVideo);
      AirfilyticsService.send('Played Video', contentMetadata, currentVideo);
    }

    function playedTimeRanges(timeRange) {
      const ranges = [];
      for (let index = 0; index < timeRange.length; index++) {
        ranges.push([timeRange.start(index), timeRange.end(index)]);
      }
      return ranges;
    }

    function logPlayerEvent(eventName, videoSessionId, currentVideo, videoPlayer) {
      const playerStatus = {
        isPlaying: !videoPlayer.paused(),
        currentTime: videoPlayer.currentTime(),
        remainingTime: videoPlayer.remainingTime(),
        duration: videoPlayer.duration()
      };
      videoPlaying = playerStatus.isPlaying;
      if (videoPlayer.played) {
        playerStatus.playedRanges = playedTimeRanges(videoPlayer.played())
      }
      if (videoPlayer.currentPlaybackBitrate) {
        playerStatus.currentPlaybackBitrate = videoPlayer.currentPlaybackBitrate();
      }
      const eventData = {
        videoSessionId,
        playerEventName: eventName,
        video: {
          id: currentVideo.id,
          title: currentVideo.labels.title,
          mediaType: currentVideo.mediaType,
          provider: currentVideo.provider
        },
        playerStatus
      }
      AirfilyticsService.send('Video Player Event', eventData);
    }

    /*
       DEPRECATED
       Keeping this for backward compatibility, logPlayerEvent will be used for logging all required player events
     */
    function logCloseVideoEvent(currentVideo) {
      videoPlaying = false;
      AirfilyticsService.send('Closed Video', {}, currentVideo);
    }

    /**
     * Pauses the video. Called when ratings popup or announcement is shown
     * If in fullscreen mode, video player exits full screen and pauses the video
     */
    function pauseVideoToShowPopup(event, player) {
      const isPlaying = !player.paused();
      if (event.name === AppConstants.BROADCASTED_EVENTS.ANNOUNCEMENT_STARTED) {
        isAnnouncementInProgress = true;
      } else if (event.name === AppConstants.BROADCASTED_EVENTS.RATINGS_POPUP_DISPLAYED) {
        isRatingsPopupShown = true;
      }

      if (player && isPlaying) {
        player.pause();
      }
      if (player.isFullscreen()) {
        player.exitFullscreen();

        // Exiting fullscreen for Android and IOS devices
        angular.element('#video-player > video')[0].webkitExitFullscreen();
      }
    }

    /**
     * Start playing the video.
     * Called when ratings popup and announcement popup is closed
     */
    function playVideoAfterClosingPopup(event, player) {
      let isPopupShown = false;
      if (event.name === AppConstants.BROADCASTED_EVENTS.ANNOUNCEMENT_STOPPED) {
        isPopupShown = isAnnouncementInProgress;
        isAnnouncementInProgress = false;
      } else if (event.name === AppConstants.BROADCASTED_EVENTS.RATINGS_POPUP_CLOSED) {
        isPopupShown = isRatingsPopupShown;
        isRatingsPopupShown = false;
      }

      if (isPopupShown) {
        isRatingsPopupShown = false;
        player.play();
      }
    }

    function getVideoMuteConfiguration() {
      const videoConfig = ApplicationService.getVideoConfig();
      return Boolean(videoConfig && videoConfig.muteVideo);
    }

    function storeVideoProgress(video, currentTime, remainingTime) {
      const storeKey = getVideoStoreKey(video);
      const jsonData = angular.toJson({
        currentTime,
        remainingTime
      });
      localStorage.setItem(storeKey, jsonData);
    }

    function getStoredVideoProgress(video) {
      const storeKey = getVideoStoreKey(video);
      const jsonData = localStorage[storeKey];
      return jsonData ? angular.fromJson(jsonData) : null;
    }

    function getVideoStoreKey(video) {
      return `video_progress_${video.id}`;
    }

    function seekVideoTime(player, secs) {
      player.currentTime(secs);
    }

    function canResumeVideo(videoProgress) {
      const END_MARGIN = 60;
      return (videoProgress && videoProgress.currentTime > 0 && videoProgress.remainingTime > END_MARGIN);
    }

    function isVideoPlaying(){
      return videoPlaying;

    }

    return {
      getPlayerTypeByVideoType,
      logCloseVideoEvent,
      logPlayedVideoEvent,
      logOpenedVideoEvent: AirfilyticsService.openedContentDetailsEvent,
      pauseVideoToShowPopup,
      playVideoAfterClosingPopup,
      getVideoMuteConfiguration,
      storeVideoProgress,
      getStoredVideoProgress,
      seekVideoTime,
      canResumeVideo,
      isVideoPlaying,
      logPlayerEvent
    };
  });
